import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [editedUser, setEditedUser] = useState({});

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  // Enable editing for a row
  const startEditing = (user) => {
    setEditingId(user.id);
    setEditedUser({ ...user });
  };

  // Handle input changes in the row
  const handleInputChange = (e, field) => {
    setEditedUser({ ...editedUser, [field]: e.target.value });
  };

  // Save changes and update the database
  const saveEdit = async () => {
    try {
      await axios.patch(`http://localhost:5000/users/${editingId}`, editedUser);
      setEditingId(null);
      getUsers(); // Refresh the user list
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div>
      <div className="container mt-5">
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div>
            <Link to="/home/AddEmployee" className="button is-success mr-1">
              Add Employee
            </Link>
            <button onClick={handleViewPDF} className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        <div className="table-container overflow-x-auto">
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Annual Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {displayedUsers.length > 0 ? (
                displayedUsers.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>
                      {editingId === user.id ? (
                        <input
                          type="text"
                          value={editedUser.full_name}
                          onChange={(e) => handleInputChange(e, "full_name")}
                          className="input is-small"
                        />
                      ) : (
                        user.full_name
                      )}
                    </td>
                    <td>
                      {editingId === user.id ? (
                        <input
                          type="email"
                          value={editedUser.email}
                          onChange={(e) => handleInputChange(e, "email")}
                          className="input is-small"
                        />
                      ) : (
                        user.email
                      )}
                    </td>
                    <td>
                      {editingId === user.id ? (
                        <input
                          type="text"
                          value={editedUser.Job_Title}
                          onChange={(e) => handleInputChange(e, "Job_Title")}
                          className="input is-small"
                        />
                      ) : (
                        user.Job_Title
                      )}
                    </td>
                    <td>
                      {editingId === user.id ? (
                        <input
                          type="text"
                          value={editedUser.position}
                          onChange={(e) => handleInputChange(e, "position")}
                          className="input is-small"
                        />
                      ) : (
                        user.position
                      )}
                    </td>
                    <td>
                      {editingId === user.id ? (
                        <input
                          type="number"
                          value={editedUser.salary}
                          onChange={(e) => handleInputChange(e, "salary")}
                          className="input is-small"
                        />
                      ) : (
                        `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                      )}
                    </td>
                    <td>
                      {editingId === user.id ? (
                        <input
                          type="number"
                          value={editedUser.anual_salary}
                          onChange={(e) => handleInputChange(e, "anual_salary")}
                          className="input is-small"
                        />
                      ) : (
                        `₱${user.anual_salary?.toLocaleString("en-PH") || "-"}`
                      )}
                    </td>
                    <td>
                      {editingId === user.id ? (
                        <button
                          className="button is-small is-success mr-2"
                          onClick={saveEdit}
                        >
                          Save
                        </button>
                      ) : (
                        <button
                          className="button is-small is-warning mr-2"
                          onClick={() => startEditing(user)}
                        >
                          Edit
                        </button>
                      )}
                      <button
                        onClick={() => openDelete(user)}
                        className="button is-small is-danger"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="8" className="has-text-centered">
                    No users found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <ConfirmBox
          open={open}
          closeDialog={() => setOpen(false)}
          title={deleteData?.full_name}
          empID={deleteData?.id}
          deleteFunction={deleteUser}
        />
      </div>
    </div>
  );
};

export default UserList;

