import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false); // Modal state
  const [editingUserId, setEditingUserId] = useState(null);
  const [editedUser, setEditedUser] = useState({});
  const [editedSalary, setEditedSalary] = useState({});
  const [editingId, setEditingId] = useState(null);
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    getUsers();
    getJobs();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Fetch job titles from API
  const getJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching job titles:", error);
    }
  };

  // Handle input changes in the edit form
  const handleEditChange = (e, field) => {
    const value = e.target.value;
    console.log(`Field: ${field}, Value: ${value}`); // Debugging
    setEditedSalary((prev) => ({ ...prev, [field]: value }));
  };

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const handleSave = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/users/${id}`, editedUser);
      setUsers(users.map((user) => (user.id === id ? editedUser : user)));
      setEditingUserId(null);
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  // Start inline editing for a salary record
  const startEditing = (salary) => {
    console.log("Editing Salary:", salary); // Debugging
    setEditingId(salary.id);

    // Ensure `job_id` is properly set by finding the job based on Job_Title
    const job = jobs.find((job) => job.Job_Title === salary.Job_Title);
    setEditedSalary({
      job_id: job ? job.id : "", // Ensure correct job_id is stored
      position: salary.position,
      salary: salary.salary,
    });
  };

  const handleCancel = () => {
    setEditingUserId(null);
    setEditedUser({});
  };

  const handleChange = (e) => {
    setEditedUser({ ...editedUser, [e.target.name]: e.target.value });
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div style={{ position: "relative" }}>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      {/* Dark Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />
      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="is-flex">
            {isMobile ? (
              <Link to="/home/AddEmployee" className="button is-success mr-1">
                Add Employee
              </Link>
            ) : (
              <div>
                <button
                  className="button is-success ml-3"
                  onClick={() => setIsModalActive(true)} // Open modal
                >
                  Add Job Title
                </button>{" "}
                <AddEmployeeModal
                  isOpen={isModalActive}
                  onClose={() => setIsModalActive(false)}
                />
              </div>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        {!isMobile ? (
          <div className="table-container overflow-x-auto">
            <table className="table is-striped is-fullwidth">
              <thead>
                <tr>
                  <th>Employee ID</th>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Job Title</th>
                  <th>Position</th>
                  <th>Salary</th>
                  <th>Annual Salary</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {displayedUsers.length > 0 ? (
                  displayedUsers.map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>
                        {editingUserId === user.id ? (
                          <input
                            type="text"
                            name="full_name"
                            className="input is-small"
                            value={editedUser.full_name}
                            onChange={handleChange}
                          />
                        ) : (
                          user.full_name
                        )}
                      </td>
                      <td>
                        {editingUserId === user.id ? (
                          <input
                            type="email"
                            name="email"
                            className="input is-small"
                            value={editedUser.email}
                            onChange={handleChange}
                          />
                        ) : (
                          user.email
                        )}
                      </td>
                      <td>
                        {editingId === user.id ? (
                          <div className="select is-fullwidth">
                            <select
                              value={editedSalary.job_id || ""}
                              onChange={(e) => handleEditChange(e, "job_id")}
                              required
                            >
                              <option value="">Select a department</option>
                              {jobs.map((dep) => (
                                <option key={dep.id} value={dep.id}>
                                  {dep.Job_Title}
                                </option>
                              ))}
                            </select>
                          </div>
                        ) : (
                          user.Job_Title
                        )}
                      </td>
                      <td>
                        {editingId === user.id ? (
                          <div className="select is-fullwidth">
                            <select
                              value={editedSalary.job_id || ""}
                              onChange={(e) => handleEditChange(e, "job_id")}
                              required
                            >
                              <option value="">Select a position</option>
                              {position.map((dep) => (
                                <option key={dep.id} value={dep.id}>
                                  {dep.Job_Title}
                                </option>
                              ))}
                            </select>
                          </div>
                        ) : (
                          user.Job_Title
                        )}
                      </td>
                      <td>₱{user.salary?.toLocaleString("en-PH") || "-"}</td>
                      <td>
                        ₱{user.anual_salary?.toLocaleString("en-PH") || "-"}
                      </td>
                      <td>
                        {editingUserId === user.id ? (
                          <>
                            <button
                              className="button is-small is-success mr-2"
                              onClick={() => handleSave(user.id)}
                            >
                              Save
                            </button>
                            <button
                              className="button is-small is-danger"
                              onClick={handleCancel}
                            >
                              Cancel
                            </button>
                          </>
                        ) : (
                          <div>
                            <button
                              className="button is-small is-info"
                              onClick={() => startEditing(user)}
                            >
                              Edit
                            </button>
                            <button
                              onClick={() => openDelete(user)}
                              className="button is-small is-danger"
                            >
                              Delete
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="8" className="has-text-centered">
                      No users found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="columns is-multiline m-2">
            {displayedUsers.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{user.full_name}</p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Employee ID:</strong> {user.id}
                    </p>
                    <p>
                      <strong>Email:</strong> {user.email}
                    </p>
                    <p>
                      <strong>Job Title:</strong> {user.Job_Title}
                    </p>
                    <p>
                      <strong>Position:</strong> {user.position}
                    </p>
                    <p>
                      <strong>Salary:</strong> ₱
                      {user.salary?.toLocaleString("en-PH") || "-"}
                    </p>
                    <p>
                      <strong>Annual Salary:</strong> ₱
                      {user.anual_salary?.toLocaleString("en-PH") || "-"}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/edit/${user.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                    <button
                      onClick={() => openDelete(user)}
                      className="card-footer-item button is-small is-danger"
                    >
                      Delete
                    </button>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 p-3 has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredUsers.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedUsers.length} of {filteredUsers.length} entries
          </p>
        </div>

        <ConfirmBox
          open={open}
          closeDialog={() => setOpen(false)}
          title={deleteData?.full_name}
          empID={deleteData?.id}
          deleteFunction={deleteUser}
        />
      </div>
    </div>
  );
};

export default UserList;
