import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";
// Corrected from AddSalaryModel
function SalaryList() {
  // State Declarations
  const [salaries, setSalaries] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({
    depId: "",
    position: "",
    salary: "",
  });
  const [jobs, setJobs] = useState([]);
  const [isAdding, setIsAdding] = useState(false);
  const [newSalary, setNewSalary] = useState({
    depId: "",
    position: "",
    salary: "",
  });
  const [addErrors, setAddErrors] = useState({
    depId: false,
    position: false,
    salary: false,
  });
  const [editErrors, setEditErrors] = useState({
    depId: false,
    position: false,
    salary: false,
  });
  const [isLoading, setIsLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");

  // Fetch Data on Mount and Handle Resize
  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const [salariesResponse, jobsResponse] = await Promise.all([
          axios.get("http://localhost:5000/salary"),
          axios.get("http://localhost:5000/job"),
        ]);
        setSalaries(salariesResponse.data || []);
        setJobs(jobsResponse.data || []);
      } catch (error) {
        console.error("Error fetching data:", error);
        setErrorMessage("Failed to load data. Please try again later.");
        setSalaries([]);
        setJobs([]);
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();

    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Data Filtering and Pagination
  const filteredSalaries = salaries.filter((user) =>
    `${user.Job_Title} ${user.position}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );
  const displayedSalaries = filteredSalaries.slice(
    0,
    entriesToShow === "All" ? filteredSalaries.length : entriesToShow
  );

  // PDF Generation
  const handleViewPDF = async () => {
    try {
      const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
      const url = URL.createObjectURL(blob);
      window.open(url);
    } catch (error) {
      console.error("Error generating PDF:", error);
      setErrorMessage("Failed to generate PDF. Please try again.");
    }
  };

  // Editing Functions
  const startEditing = (salary) => {
    const newEditedSalary = {
      depId: salary.job_id || "", // Use job_id directly, assuming it's in the salary data
      position: salary.position || "",
      salary: salary.salary || "",
    };
    setEditingId(salary.id);
    setEditedSalary(newEditedSalary);
    setEditErrors({ depId: false, position: false, salary: false });
  };

  const cancelEditing = () => {
    setEditingId(null);
    setEditedSalary({ depId: "", position: "", salary: "" });
    setEditErrors({ depId: false, position: false, salary: false });
  };

  const handleEditChange = (e, field) => {
    const value = e.target.value;
    setEditedSalary((prev) => ({ ...prev, [field]: value }));
    setEditErrors((prev) => ({
      ...prev,
      [field]:
        field === "salary"
          ? !value || parseFloat(value) <= 0
          : value.trim() === "",
    }));
  };

  // In saveNewSalary
  const saveNewSalary = async () => {
    const newAddErrors = {
      depId: !newSalary.depId || newSalary.depId.trim() === "",
      position: !newSalary.position || newSalary.position.trim() === "",
      salary: !newSalary.salary || parseFloat(newSalary.salary) <= 0,
    };
    setAddErrors(newAddErrors);

    if (Object.values(newAddErrors).some((error) => error)) {
      return;
    }

    try {
      await axios.post("http://localhost:5000/salary", {
        job_id: parseInt(newSalary.depId, 10), // Changed from depId to job_id
        position: newSalary.position,
        salary: parseFloat(newSalary.salary),
      });
      const response = await axios.get("http://localhost:5000/salary");
      setSalaries(response.data || []);
      cancelAdding();
    } catch (error) {
      console.error("Error adding salary:", error);
      setErrorMessage("Failed to add salary. Please try again.");
    }
  };

  // In saveSalary
  const saveSalary = async (id) => {
    const newEditErrors = {
      depId: !editedSalary.depId || editedSalary.depId.trim() === "",
      position: !editedSalary.position || editedSalary.position.trim() === "",
      salary: !editedSalary.salary || parseFloat(editedSalary.salary) <= 0,
    };
    setEditErrors(newEditErrors);

    if (Object.values(newEditErrors).some((error) => error)) {
      return;
    }

    try {
      await axios.patch(`http://localhost:5000/salary/${id}`, {
        job_id: parseInt(editedSalary.depId, 10), // Changed from depId to job_id
        position: editedSalary.position,
        salary: parseFloat(editedSalary.salary),
      });
      const response = await axios.get("http://localhost:5000/salary");
      setSalaries(response.data || []);
      cancelEditing();
    } catch (error) {
      console.error("Error updating salary:", error);
      setErrorMessage("Failed to update salary. Please try again.");
    }
  };

  // Adding Functions
  const startAdding = () => setIsAdding(true);

  const cancelAdding = () => {
    setIsAdding(false);
    setNewSalary({ depId: "", position: "", salary: "" });
    setAddErrors({ depId: false, position: false, salary: false });
  };

  const handleNewChange = (e, field) => {
    const value = e.target.value;
    setNewSalary((prev) => ({ ...prev, [field]: value }));
    setAddErrors((prev) => ({
      ...prev,
      [field]:
        field === "salary"
          ? !value || parseFloat(value) <= 0
          : value.trim() === "",
    }));
  };

  // Render
  return (
    <div style={{ position: "relative" }}>
      {/* Background Image */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      {/* Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        {/* Error Message */}
        {errorMessage && (
          <div className="notification is-danger">{errorMessage}</div>
        )}

        {/* Search and Buttons */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input mt-2"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            aria-label="Search salaries"
          />
          <div className="mt-2 is-flex">
            {!isMobile && !isAdding && (
              <button className="button is-success ml-3" onClick={startAdding}>
                Add Position
              </button>
            )}
            {isMobile && (
              <Link to="/home/AddSalary" className="button is-success ml-3">
                Add Position
              </Link>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-3">
              View PDF
            </button>
          </div>
        </div>

        {/* Content */}
        {isLoading ? (
          <div className="has-text-centered">Loading...</div>
        ) : !isMobile ? (
          /* Desktop Table View */
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Salary ID</th>
                <th>Job Title</th>
                <th>Position / Level</th>
                <th>Salary</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {isAdding && (
                <tr>
                  <td>New</td>
                  <td>
                    <div className="select is-fullwidth">
                      <select
                        value={newSalary.depId}
                        onChange={(e) => handleNewChange(e, "depId")}
                        style={{ borderColor: addErrors.depId ? "red" : "" }}
                        aria-label="Select Job Title"
                      >
                        <option value="">Select a Job</option>
                        {jobs.map((job) => (
                          <option key={job.id} value={job.id}>
                            {job.Job_Title}
                          </option>
                        ))}
                      </select>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newSalary.position}
                      onChange={(e) => handleNewChange(e, "position")}
                      placeholder="Position / Level"
                      style={{ borderColor: addErrors.position ? "red" : "" }}
                      aria-label="Position"
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      step="0.01"
                      className="input"
                      value={newSalary.salary}
                      onChange={(e) => handleNewChange(e, "salary")}
                      placeholder="Salary"
                      style={{ borderColor: addErrors.salary ? "red" : "" }}
                      aria-label="Salary"
                    />
                  </td>
                  <td>
                    <button
                      className="button is-small is-success mr-2"
                      onClick={saveNewSalary}
                    >
                      Save
                    </button>
                    <button
                      className="button is-small is-danger"
                      onClick={cancelAdding}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              )}
              {displayedSalaries.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>
                    {editingId === user.id ? (
                      <div className="select is-fullwidth">
                        <select
                          value={editedSalary.depId || ""}
                          onChange={(e) => handleEditChange(e, "depId")}
                          style={{ borderColor: editErrors.depId ? "red" : "" }}
                          aria-label="Edit Job Title"
                        >
                          <option value="">Select a Job</option>
                          {jobs.map((job) => (
                            <option key={job.id} value={job.id}>
                              {job.Job_Title}
                            </option>
                          ))}
                        </select>
                      </div>
                    ) : (
                      user.Job_Title
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedSalary.position || ""}
                        onChange={(e) => handleEditChange(e, "position")}
                        style={{
                          borderColor: editErrors.position ? "red" : "",
                        }}
                        aria-label="Edit Position"
                      />
                    ) : (
                      user.position
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="number"
                        step="0.01"
                        className="input"
                        value={editedSalary.salary || ""}
                        onChange={(e) => handleEditChange(e, "salary")}
                        style={{ borderColor: editErrors.salary ? "red" : "" }}
                        aria-label="Edit Salary"
                      />
                    ) : (
                      `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                        maximumFractionDigits: 2,
                      })}`
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <>
                        <button
                          className="button is-small is-success mr-2"
                          onClick={() => saveSalary(user.id)}
                        >
                          Save
                        </button>
                        <button
                          className="button is-small is-danger"
                          onClick={cancelEditing}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="button is-small is-info"
                        onClick={() => startEditing(user)}
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          /* Mobile Card View */
          <div className="columns is-multiline">
            {displayedSalaries.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{user.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Salary ID:</strong> {user.id}
                    </p>
                    <p>
                      <strong>Position:</strong> {user.position || "N/A"}
                    </p>
                    <p>
                      <strong>Salary:</strong>{" "}
                      {user.salary
                        ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                            minimumFractionDigits: 2,
                            maximumFractionDigits: 2,
                          })}`
                        : "N/A"}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/editSalary/${user.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Pagination */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2" htmlFor="entries-select">
              Show entries:
            </label>
            <div className="select">
              <select
                id="entries-select"
                value={entriesToShow}
                onChange={(e) =>
                  setEntriesToShow(
                    e.target.value === "All" ? "All" : Number(e.target.value)
                  )
                }
                aria-label="Select number of entries to show"
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value="All">All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedSalaries.length} of {filteredSalaries.length}{" "}
            entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default SalaryList;
