import React, { useState } from "react";
import axios from "axios";

function AddDeptModal({ isActive, onClose, getUsers }) {
  const [Department_Name, setDept] = useState("");
  const [successMessage, setSuccessMessage] = useState(""); // Success message state

  const saveDepartment = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/job", { Department_Name });
      
      setSuccessMessage("Job Title added successfully!"); // Show success message
      setDept(""); // Clear input field
      getUsers(); // Refresh the job list in the parent component
    } catch (error) {
      console.log(error);
    }
  };

  const handleClose = () => {
    getUsers(); // Ensure the table updates when modal closes
    onClose();
  };

  return (
    <div className={`modal ${isActive ? "is-active" : ""}`}>
      <div className="modal-background" onClick={handleClose}></div>
      <div className="modal-card">
        <header className="modal-card-head">
          <p className="modal-card-title">Add Job Title</p>
          <button className="delete" aria-label="close" onClick={handleClose}></button>
        </header>
        <section className="modal-card-body">
          <form onSubmit={saveDepartment}>
            <div className="field">
              <label className="label">Job Title</label>
              <div className="control">
                <input
                  type="text"
                  className="input"
                  value={Department_Name}
                  onChange={(e) => setDept(e.target.value)}
                  placeholder="Enter Job Title"
                  required
                />
              </div>
            </div>
            {successMessage && (
              <div className="notification is-success">{successMessage}</div>
            )}
          </form>
        </section>
        <footer className="modal-card-foot">
          <button type="submit" className="button is-success" onClick={saveDepartment}>
            Save
          </button>
          <button className="button" onClick={handleClose}>Cancel</button>
        </footer>
      </div>
    </div>
  );
}

export default AddDeptModal;
