import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false);
  const [editRow, setEditRow] = useState(null);
  const [editedData, setEditedData] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  const handleEdit = (user) => {
    setEditRow(user.id);
    setEditedData({ ...user });
  };

  const handleInputChange = (e, field) => {
    setEditedData({ ...editedData, [field]: e.target.value });
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:5000/users/${id}`, editedData);
      setEditRow(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="is-flex">
            {isMobile ? (
              <Link to="/home/AddEmployee" className="button is-success mr-1">
                Add Employee
              </Link>
            ) : (
              <div>
                <button className="button is-success ml-3" onClick={() => setIsModalActive(true)}>
                  Add Job Title
                </button>
                <AddEmployeeModal isOpen={isModalActive} onClose={() => setIsModalActive(false)} />
              </div>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        {!isMobile ? (
          <div className="table-container overflow-x-auto">
            <table className="table is-striped is-fullwidth">
              <thead>
                <tr>
                  <th>Employee ID</th>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Job Title</th>
                  <th>Position</th>
                  <th>Salary</th>
                  <th>Annual Salary</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {displayedUsers.length > 0 ? (
                  displayedUsers.map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            value={editedData.full_name}
                            onChange={(e) => handleInputChange(e, "full_name")}
                          />
                        ) : (
                          user.full_name
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            value={editedData.email}
                            onChange={(e) => handleInputChange(e, "email")}
                          />
                        ) : (
                          user.email
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            value={editedData.Job_Title}
                            onChange={(e) => handleInputChange(e, "Job_Title")}
                          />
                        ) : (
                          user.Job_Title
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            value={editedData.position}
                            onChange={(e) => handleInputChange(e, "position")}
                          />
                        ) : (
                          user.position
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="number"
                            value={editedData.salary}
                            onChange={(e) => handleInputChange(e, "salary")}
                          />
                        ) : (
                          `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                        )}
                      </td>
                      <td>
                        <button onClick={() => (editRow === user.id ? handleSave(user.id) : handleEdit(user))} className="button is-small is-info">
                          {editRow === user.id ? "Save" : "Edit"}
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="8" className="has-text-centered">
                      No users found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        ) : null}
      </div>
    </div>
  );
};

export default UserList;
