import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editRowId, setEditRowId] = useState(null);
  const [editedUser, setEditedUser] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  const handleEdit = (user) => {
    setEditRowId(user.id);
    setEditedUser({ ...user });
  };

  const handleInputChange = (e, field) => {
    setEditedUser({ ...editedUser, [field]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/users/${editRowId}`, editedUser);
      setEditRowId(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div>
            <Link to="/home/AddEmployee" className="button is-success mr-1">
              Add Employee
            </Link>
            <button onClick={handleViewPDF} className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        {!isMobile ? (
          <div className="table-container overflow-x-auto">
            <table className="table is-striped is-fullwidth">
              <thead>
                <tr>
                  <th>Employee ID</th>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Job Title</th>
                  <th>Position</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {displayedUsers.length > 0 ? (
                  displayedUsers.map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>
                        {editRowId === user.id ? (
                          <input
                            type="text"
                            className="input"
                            value={editedUser.full_name}
                            onChange={(e) => handleInputChange(e, "full_name")}
                          />
                        ) : (
                          user.full_name
                        )}
                      </td>
                      <td>{user.email}</td>
                      <td>
                        {editRowId === user.id ? (
                          <div className="select">
                            <select
                              value={editedUser.Job_Title}
                              onChange={(e) => handleInputChange(e, "Job_Title")}
                            >
                              <option value="Developer">Developer</option>
                              <option value="Manager">Manager</option>
                            </select>
                          </div>
                        ) : (
                          user.Job_Title
                        )}
                      </td>
                      <td>
                        {editRowId === user.id ? (
                          <div className="select">
                            <select
                              value={editedUser.position}
                              onChange={(e) => handleInputChange(e, "position")}
                            >
                              <option value="Junior">Junior</option>
                              <option value="Senior">Senior</option>
                            </select>
                          </div>
                        ) : (
                          user.position
                        )}
                      </td>
                      <td>
                        {editRowId === user.id ? (
                          <button onClick={handleSave} className="button is-small is-success">
                            Save
                          </button>
                        ) : (
                          <button
                            onClick={() => handleEdit(user)}
                            className="button is-small is-warning mr-2"
                          >
                            Edit
                          </button>
                        )}
                        <button onClick={() => openDelete(user)} className="button is-small is-danger">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="6" className="has-text-centered">
                      No users found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="columns is-multiline m-2">
            {displayedUsers.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{user.full_name}</p>
                  </header>
                  <div className="card-content">
                    <p><strong>Email:</strong> {user.email}</p>
                    <p><strong>Job Title:</strong> {user.Job_Title}</p>
                    <p><strong>Position:</strong> {user.position}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        <ConfirmBox open={open} closeDialog={() => setOpen(false)} deleteFunction={deleteUser} />
      </div>
    </div>
  );
};

export default UserList;

