import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [departments, setDepartments] = useState([]);
  const [positions, setPositions] = useState({});
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    getUsers();
    fetchDepartments();

    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDepartments(response.data);
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchPositions = async (dep_id) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/salary/getPosition/${dep_id}`
      );
      setPositions((prev) => ({ ...prev, [dep_id]: response.data }));
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const handleEdit = (user) => {
    setEditingUser(user);
    fetchPositions(user.job_id);
  };

  const handleChange = (e, field) => {
    setEditingUser({ ...editingUser, [field]: e.target.value });
  };

  const handleSave = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/users/${id}`, {
        full_name: editingUser.full_name,
        email: editingUser.email,
        job_id: editingUser.job_id,
        position_id: editingUser.position_id,
      });
      setEditingUser(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  return (
    <div style={{ position: "relative" }}>
      {/* Background */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div>
            <Link to="/home/AddEmployee" className="button is-success mr-1">
              Add Employee
            </Link>
          </div>
        </div>

        <div className="table-container overflow-x-auto">
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {displayedUsers.length > 0 ? (
                displayedUsers.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>
                      {editingUser?.id === user.id ? (
                        <input
                          type="text"
                          className="input"
                          value={editingUser.full_name}
                          onChange={(e) => handleChange(e, "full_name")}
                        />
                      ) : (
                        user.full_name
                      )}
                    </td>
                    <td>
                      {editingUser?.id === user.id ? (
                        <input
                          type="email"
                          className="input"
                          value={editingUser.email}
                          onChange={(e) => handleChange(e, "email")}
                        />
                      ) : (
                        user.email
                      )}
                    </td>
                    <td>
                      {editingUser?.id === user.id ? (
                        <select
                          className="input"
                          value={editingUser.job_id}
                          onChange={(e) => {
                            handleChange(e, "job_id");
                            fetchPositions(e.target.value);
                          }}
                        >
                          {departments.map((dep) => (
                            <option key={dep.id} value={dep.id}>
                              {dep.Job_Title}
                            </option>
                          ))}
                        </select>
                      ) : (
                        user.Job_Title
                      )}
                    </td>
                    <td>
                      {editingUser?.id === user.id ? (
                        <select
                          className="input"
                          value={editingUser.position_id}
                          onChange={(e) => handleChange(e, "position_id")}
                        >
                          {(positions[editingUser.job_id] || []).map((pos) => (
                            <option key={pos.id} value={pos.id}>
                              {pos.position}
                            </option>
                          ))}
                        </select>
                      ) : (
                        user.position
                      )}
                    </td>
                    <td>
                      {editingUser?.id === user.id ? (
                        <button
                          onClick={() => handleSave(user.id)}
                          className="button is-small is-success"
                        >
                          Save
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(user)}
                          className="button is-small is-info mr-2"
                        >
                          Edit
                        </button>
                      )}
                      <button
                        onClick={() => setOpen(true) && setDeleteData(user)}
                        className="button is-small is-danger"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="has-text-centered">
                    No users found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <ConfirmBox
          open={open}
          closeDialog={() => setOpen(false)}
          title={deleteData?.full_name}
          empID={deleteData?.id}
          deleteFunction={() => {}}
        />
      </div>
    </div>
  );
};

export default UserList;

