import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  const [salaries, setSalary] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [jobs, setJobs] = useState([]);
  const [addingRow, setAddingRow] = useState(false); // Controls new row visibility
  const [newSalary, setNewSalary] = useState({ job_id: "", position: "", salary: "" });

  useEffect(() => {
    getUsers();
    getJobs();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      setSalary(response.data);
    } catch (error) {
      console.error("Error fetching salaries:", error);
    }
  };

  const getJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching job titles:", error);
    }
  };

  const handleAddRow = () => {
    setAddingRow(true);
    setNewSalary({ job_id: "", position: "", salary: "" });
  };

  const handleNewSalaryChange = (e, field) => {
    setNewSalary((prev) => ({ ...prev, [field]: e.target.value }));
  };

  const saveNewSalary = async () => {
    try {
      await axios.post("http://localhost:5000/salary", {
        dep_id: newSalary.job_id,
        position: newSalary.position,
        salary: newSalary.salary,
      });
      getUsers(); // Refresh table
      setAddingRow(false);
    } catch (error) {
      console.error("Error saving salary:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      {/* Background */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        {/* Search and Actions */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input mt-2"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="mt-2 is-flex">
            <button className="button is-success ml-3" onClick={handleAddRow}>
              Add Job Salary
            </button>
            <button onClick={() => handleViewPDF()} className="button is-info ml-3">
              View PDF
            </button>
          </div>
        </div>

        {/* Table */}
        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Salary ID</th>
                <th>Job Title</th>
                <th>Position / Level</th>
                <th>Salary</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {/* Existing Data */}
              {salaries.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.Job_Title}</td>
                  <td>{user.position}</td>
                  <td>{`â‚±${parseFloat(user.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}`}</td>
                  <td>
                    <button className="button is-small is-info">Edit</button>
                  </td>
                </tr>
              ))}

              {/* New Row for Adding */}
              {addingRow && (
                <tr>
                  <td>New</td>
                  <td>
                    <div className="select is-fullwidth">
                      <select
                        value={newSalary.job_id}
                        onChange={(e) => handleNewSalaryChange(e, "job_id")}
                        required
                      >
                        <option value="">Select a Job Title</option>
                        {jobs.map((job) => (
                          <option key={job.id} value={job.id}>
                            {job.Job_Title}
                          </option>
                        ))}
                      </select>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newSalary.position}
                      onChange={(e) => handleNewSalaryChange(e, "position")}
                      placeholder="Position / Level"
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="input"
                      value={newSalary.salary}
                      onChange={(e) => handleNewSalaryChange(e, "salary")}
                      placeholder="Salary"
                      required
                    />
                  </td>
                  <td>
                    <button className="button is-small is-success mr-2" onClick={saveNewSalary}>
                      Save
                    </button>
                    <button className="button is-small is-danger" onClick={() => setAddingRow(false)}>
                      Cancel
                    </button>
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        ) : (
          <p>Mobile version remains unchanged.</p>
        )}

        {/* Footer Controls */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={salaries.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {salaries.length} entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default SalaryList;
