import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  // State Hooks
  const [salaries, setSalaries] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  
  // New Salary Entry State
  const [newSalary, setNewSalary] = useState({
    job_id: "",
    position: "",
    salary: "",
  });

  // Effect Hook for fetching data and handling window resize
  useEffect(() => {
    fetchSalaries();
    fetchJobs();

    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Fetch salary data from API
  const fetchSalaries = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      setSalaries(response.data);
    } catch (error) {
      console.error("Error fetching salaries:", error);
    }
  };

  // Fetch job titles from API
  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching job titles:", error);
    }
  };

  // Handle changes in the new salary input row
  const handleNewSalaryChange = (e, field) => {
    setNewSalary({ ...newSalary, [field]: e.target.value });
  };

  // Save new salary entry
  const saveNewSalary = async () => {
    if (!newSalary.job_id || !newSalary.position || !newSalary.salary) {
      alert("Please fill all fields!");
      return;
    }

    try {
      await axios.post("http://localhost:5000/salary", {
        depId: newSalary.job_id,
        position: newSalary.position,
        salary: newSalary.salary,
      });

      // Refresh the table and reset the newSalary state
      fetchSalaries();
      setNewSalary({ job_id: "", position: "", salary: "" });
    } catch (error) {
      console.error("Error saving new salary:", error);
    }
  };

  // Start inline editing for an existing salary record
  const startEditing = (salary) => {
    setEditingId(salary.id);
    const job = jobs.find((job) => job.Job_Title === salary.Job_Title);
    setEditedSalary({
      job_id: job ? job.id : "",
      position: salary.position,
      salary: salary.salary,
    });
  };

  // Cancel editing
  const cancelEditing = () => {
    setEditingId(null);
    setEditedSalary({});
  };

  // Save updated salary record
  const saveSalary = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/salary/${id}`, {
        depId: editedSalary.job_id,
        position: editedSalary.position,
        salary: editedSalary.salary,
      });

      fetchSalaries();
      setEditingId(null);
    } catch (error) {
      console.error("Error updating salary:", error);
    }
  };

  return (
    <div className="container mt-5">
      <div className="is-flex is-justify-content-space-between mb-3">
        <input
          type="text"
          className="input"
          placeholder="Search by Job Title or Position..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={saveNewSalary} className="button is-success ml-3">
          Save New Salary
        </button>
      </div>

      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Salary ID</th>
            <th>Job Title</th>
            <th>Position / Level</th>
            <th>Salary</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {/* New Salary Entry Row */}
          <tr>
            <td>New</td>
            <td>
              <div className="select is-fullwidth">
                <select
                  value={newSalary.job_id}
                  onChange={(e) => handleNewSalaryChange(e, "job_id")}
                >
                  <option value="">Select a Job</option>
                  {jobs.map((job) => (
                    <option key={job.id} value={job.id}>
                      {job.Job_Title}
                    </option>
                  ))}
                </select>
              </div>
            </td>
            <td>
              <input
                type="text"
                className="input"
                placeholder="Position / Level"
                value={newSalary.position}
                onChange={(e) => handleNewSalaryChange(e, "position")}
              />
            </td>
            <td>
              <input
                type="number"
                className="input"
                placeholder="Salary"
                value={newSalary.salary}
                onChange={(e) => handleNewSalaryChange(e, "salary")}
              />
            </td>
            <td>
              <button onClick={saveNewSalary} className="button is-small is-success">
                Add
              </button>
            </td>
          </tr>

          {/* Existing Salary Records */}
          {salaries.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.Job_Title}</td>
              <td>{user.position}</td>
              <td>â‚±{parseFloat(user.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}</td>
              <td>
                <button className="button is-small is-info" onClick={() => startEditing(user)}>
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default SalaryList;
