import React, { useState } from "react";
import axios from "axios";

function AddDeptModal({ isActive, onClose, onSave }) {
  const [Department_Name, setDept] = useState("");
  const [isSaving, setIsSaving] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");

  const saveDepartment = async (e) => {
    e.preventDefault();
    setIsSaving(true);

    try {
      const response = await axios.post("http://localhost:5000/job", { Department_Name });
      
      setDept(""); // Clear input field
      setSuccessMessage("Successfully added!"); // Show success message
      
      // Call the parent function to refresh the department list
      if (onSave) onSave(response.data); 

      setTimeout(() => setSuccessMessage(""), 3000); // Hide message after 3s
    } catch (error) {
      console.error(error);
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className={`modal ${isActive ? "is-active" : ""}`}>
      <div className="modal-background" onClick={onClose}></div>
      <div className="modal-card">
        <header className="modal-card-head">
          <p className="modal-card-title">Add Job Title</p>
          <button className="delete" aria-label="close" onClick={onClose}></button>
        </header>
        <section className="modal-card-body">
          <form onSubmit={saveDepartment}>
            <div className="field">
              <label className="label">Job Title</label>
              <div className="control">
                <input
                  type="text"
                  className="input"
                  value={Department_Name}
                  onChange={(e) => setDept(e.target.value)}
                  placeholder="Enter Job Title"
                  required
                />
              </div>
            </div>
          </form>
          {successMessage && (
            <div className="notification is-success mt-3">{successMessage}</div>
          )}
        </section>
        <footer className="modal-card-foot">
          <button
            type="submit"
            className={`button is-success ${isSaving ? "is-loading" : ""}`}
            onClick={saveDepartment}
            disabled={isSaving}
          >
            {isSaving ? "Saving..." : "Save"}
          </button>
          <button className="button" onClick={onClose}>Cancel</button>
        </footer>
      </div>
    </div>
  );
}

export default AddDeptModal;
