import React, { useState } from "react";
import { motion } from "framer-motion";

const projects = [
  { title: "Library System with Inventory & QR Code", img: "/BMALibrary.png", hoverImg: "/BMALibraryHover.png" },
  { title: "BMA Portal", img: "/BMALanding.png", hoverImg: "/BMALibrary.png" },
  { title: "MilkTea Inventory & POS", img: "/milktea.png", hoverImg: "/milkteaHover.png" },
  { title: "BMA Attendance QR-Code", img: "", hoverImg: "" },
  { title: "Mini-Grocery", img: "/POSANDINVENTORY.jpg", hoverImg: "/POSANDINVENTORYHover.jpg" },
  { title: "Clearance System", img: "/cleranceSystem.png", hoverImg: "/cleranceSystemHover.png" },
];

const Resume = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [hoveredIndex, setHoveredIndex] = useState(null); // ðŸ”¥ Store hovered index to prevent glitching

  return (
    <div
      className="is-flex is-justify-content-center is-align-items-center"
      style={{
        minHeight: "100vh",
        backgroundImage: "url('/loginBanner.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        padding: "3rem",
      }}
    >
      <motion.div
        initial={{ opacity: 0, scale: 0.9, y: 20 }}
        animate={{ opacity: 1, scale: 1, y: 0 }}
        transition={{ duration: 0.6, ease: "easeOut" }}
        className={`box ${darkMode ? "has-background-black has-text-light" : "has-background-white has-text-dark"}`}
        style={{
          maxWidth: "1000px",
          width: "100%",
          padding: "2.5rem",
          borderRadius: "16px",
          boxShadow: darkMode ? "0 10px 40px rgba(255,255,255,0.15)" : "0 10px 40px rgba(0,0,0,0.15)",
          backgroundColor: darkMode ? "rgba(0, 0, 0, 0.85)" : "rgba(255, 255, 255, 0.9)",
          backdropFilter: "blur(10px)",
          transition: "background 0.5s, color 0.5s",
        }}
      >
        {/* Toggle Dark Mode */}
        <div className="has-text-right">
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className={`button ${darkMode ? "is-light" : "is-dark"}`}
            onClick={() => setDarkMode(!darkMode)}
          >
            {darkMode ? "Light Mode" : "Dark Mode"}
          </motion.button>
        </div>

        {/* Profile Image */}
        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="has-text-centered mb-4"
        >
          <img
            src="/profile.jpg"
            alt="Ryan Santiago"
            style={{
              borderRadius: "50%",
              width: "140px",
              height: "140px",
              objectFit: "cover",
              border: darkMode ? "3px solid #fff" : "3px solid #ccc",
              boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
            }}
          />
        </motion.div>

        <motion.h2
          className="title is-4 has-text-centered"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          Ryan C. Santiago
        </motion.h2>
        <motion.p
          className="subtitle is-6 has-text-centered"
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          Programmer & IT Staff
        </motion.p>

        <hr className={darkMode ? "has-background-grey-light" : "has-background-grey-dark"} />

        {/* Projects Section */}
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
          <h3 className="title is-5">Example Projects</h3>
          <ul style={{ paddingLeft: "1rem" }}>
            {projects.map((project, index) => (
              <motion.li
                key={index}
                whileHover={{ scale: 1.05 }}
                onMouseEnter={() => setHoveredIndex(index)}
                onMouseLeave={() => setHoveredIndex(null)}
                style={{ listStyleType: "none", marginBottom: "1rem", cursor: "pointer" }}
              >
                {project.title}
                <br />
                {project.img ? (
                  <img
                    src={hoveredIndex === index && project.hoverImg ? project.hoverImg : project.img}
                    alt={project.title}
                    style={{
                      width: "100%",
                      borderRadius: "8px",
                      transition: "opacity 0.3s ease-in-out, transform 0.3s ease-in-out",
                      opacity: hoveredIndex === index ? 0.85 : 1, // âœ… Smooth opacity change
                      transform: hoveredIndex === index ? "scale(1.05)" : "scale(1)", // âœ… Prevent flickering
                    }}
                  />
                ) : (
                  <p>No Image Available</p>
                )}
              </motion.li>
            ))}
          </ul>
        </motion.div>

        {/* Contact Section */}
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.3 }}>
          <h3 className="title is-5">Contact</h3>
          <p><strong>Location:</strong> Taal, Pulilan, Bulacan</p>
          <p><strong>Phone:</strong> 0926-695-5822</p>
          <p><strong>Email:</strong> santiagoryan788@gmail.com</p>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default Resume;
