import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function DepartList() {
  const [jobs, setJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingJobId, setEditingJobId] = useState(null);
  const [editedJobTitle, setEditedJobTitle] = useState("");
  const [newJobTitle, setNewJobTitle] = useState(""); // For adding new job
  const [isAdding, setIsAdding] = useState(false); // Toggle add row

  useEffect(() => {
    fetchJobs();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      console.log("Fetched jobs:", response.data);
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching job titles:", error);
    }
  };

  const startAdding = () => {
    setIsAdding(true);
    setNewJobTitle("");
  };

  const cancelAdding = () => {
    setIsAdding(false);
    setNewJobTitle("");
  };

  const saveNewJob = async () => {
    if (!newJobTitle.trim()) {
      alert("Job Title is required");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/job", {
        Job_Title: newJobTitle,
      });
      console.log("New job added:", response.data);
      setJobs([...jobs, response.data]); // Add new job to state
      setNewJobTitle("");
      setIsAdding(false);
      fetchJobs(); // Refresh the list
      alert("Job Title added successfully!");
    } catch (error) {
      console.error("Error adding job title:", error);
      alert("Failed to add Job Title: " + (error.response?.data?.message || error.message));
    }
  };

  const startEditing = (job) => {
    setEditingJobId(job.id);
    setEditedJobTitle(job.Job_Title);
  };

  const cancelEditing = () => {
    setEditingJobId(null);
    setEditedJobTitle("");
  };

  const saveJobTitle = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/job/${id}`, {
        job_title: editedJobTitle,
      });
      setJobs(
        jobs.map((job) =>
          job.id === id ? { ...job, Job_Title: editedJobTitle } : job
        )
      );
      setEditingJobId(null);
      alert("Job title updated successfully!");
    } catch (error) {
      console.error("Error updating job title:", error);
      alert("Failed to update job title");
    }
  };

  const filteredJobs = jobs.filter((job) =>
    job.Job_Title.toLowerCase().includes(searchTerm.toLowerCase())
  );
  const displayedJobs = filteredJobs.slice(0, entriesToShow);

  return (
    <div style={{ position: "relative" }}>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search Job Title..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          {!isMobile && !isAdding && (
            <button
              className="button is-success ml-3"
              onClick={startAdding}
            >
              Add Job Title
            </button>
          )}
          {isMobile && (
            <Link to="/home/AddJob" className="button is-success ml-3">
              Add Job Title
            </Link>
          )}
        </div>

        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Job ID</th>
                <th>Job Title</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {isAdding && (
                <tr>
                  <td>New</td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newJobTitle}
                      onChange={(e) => setNewJobTitle(e.target.value)}
                      placeholder="Enter new job title"
                    />
                  </td>
                  <td>
                    <button
                      className="button is-small is-success mr-2"
                      onClick={saveNewJob}
                    >
                      Save
                    </button>
                    <button
                      className="button is-small is-danger"
                      onClick={cancelAdding}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              )}
              {displayedJobs.map((job) => (
                <tr key={job.id}>
                  <td>{job.id}</td>
                  <td>
                    {editingJobId === job.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedJobTitle}
                        onChange={(e) => setEditedJobTitle(e.target.value)}
                      />
                    ) : (
                      job.Job_Title
                    )}
                  </td>
                  <td>
                    {editingJobId === job.id ? (
                      <>
                        <button
                          className="button is-small is-success mr-2"
                          onClick={() => saveJobTitle(job.id)}
                        >
                          Save
                        </button>
                        <button
                          className="button is-small is-danger"
                          onClick={cancelEditing}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="button is-small is-info mr-2"
                        onClick={() => startEditing(job)}
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="columns is-multiline">
            {displayedJobs.map((job) => (
              <div key={job.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{job.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p className="subtitle is-6">
                      <strong>Job ID:</strong> {job.id}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/editJobTitle/${job.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredJobs.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedJobs.length} of {filteredJobs.length} entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default DepartList;
