import React, { useState, useEffect } from "react";
import axios from "axios";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [editRow, setEditRow] = useState(null);
  const [editedData, setEditedData] = useState({});
  const [positions, setPositions] = useState([]);
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    getUsers();
    fetchDepartments();
  }, []);

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDepartments(response.data);
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  useEffect(() => {
    if (editedData.job_id) {
      fetchPositions(editedData.job_id);
    }
  }, [editedData.job_id]);

  const fetchPositions = async (job_id) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/salary/getPosition/${job_id}`
      );
      setPositions(response.data);
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const handleEdit = (user) => {
    setEditRow(user.id);
    setEditedData({
      full_name: user.full_name,
      email: user.email,
      job_id: user.job_id,
      position_id: user.position_id,
      salary: user.salary,
    });
  };

  const handleEditChange = (e, field) => {
    const value = e.target.value;
    setEditedData((prev) => ({ ...prev, [field]: value }));
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:5000/users/${id}`, editedData);
      setEditRow(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div className="container mt-5">
      <div className="is-flex is-justify-content-space-between m-2">
        <input
          type="text"
          className="input"
          style={{ maxWidth: "300px" }}
          placeholder="Search by Full Name, Job Title or Position..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleViewPDF} className="button is-info ml-1">
          View PDF
        </button>
      </div>

      <div className="table-container overflow-x-auto">
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>Employee ID</th>
              <th>Full Name</th>
              <th>Email</th>
              <th>Job Title</th>
              <th>Position</th>
              <th>Salary</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>
                  {editRow === user.id ? (
                    <input
                      type="text"
                      className="input"
                      value={editedData.full_name}
                      onChange={(e) => handleEditChange(e, "full_name")}
                    />
                  ) : (
                    user.full_name
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <input
                      type="text"
                      className="input"
                      value={editedData.email}
                      onChange={(e) => handleEditChange(e, "email")}
                    />
                  ) : (
                    user.email
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <select
                      value={editedData.job_id}
                      onChange={(e) => handleEditChange(e, "job_id")}
                    >
                      {departments.map((dep) => (
                        <option key={dep.id} value={dep.id}>
                          {dep.Job_Title}
                        </option>
                      ))}
                    </select>
                  ) : (
                    user.Job_Title
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <select
                      value={editedData.position_id}
                      onChange={(e) => handleEditChange(e, "position_id")}
                    >
                      {positions.map((pos) => (
                        <option key={pos.id} value={pos.id}>
                          {pos.position}
                        </option>
                      ))}
                    </select>
                  ) : (
                    user.position
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <input
                      type="number"
                      className="input"
                      value={editedData.salary}
                      onChange={(e) => handleEditChange(e, "salary")}
                    />
                  ) : (
                    `₱${user.salary?.toLocaleString() || "-"}`
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <button
                      onClick={() => handleSave(user.id)}
                      className="button is-small is-success"
                    >
                      Save
                    </button>
                  ) : (
                    <button
                      onClick={() => handleEdit(user)}
                      className="button is-small is-info"
                    >
                      Edit
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default UserList;





