import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  // State Hooks
  const [salaries, setSalaries] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  const [jobs, setJobs] = useState([]);
  const [newSalary, setNewSalary] = useState({
    job_id: "",
    position: "",
    salary: "",
  });
  const [isAdding, setIsAdding] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Axios configuration
  axios.defaults.withCredentials = true;

  // Normalize salary data with flexible field handling
  const normalizeSalary = (salary) => {
    console.log("Normalizing salary:", salary); // Debug incoming data
    return {
      id: salary.id || null,
      Job_Title: salary.Job_Title || salary.job_title || "Untitled",
      position: salary.position || "N/A",
      salary: salary.salary || "0",
    };
  };

  // Fetch data with retry logic
  useEffect(() => {
    fetchData();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const fetchData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      console.log("Attempting to fetch data from server...");
      await Promise.all([fetchSalaries(), fetchJobs()]);
      console.log("Data fetch successful!");
    } catch (err) {
      console.error("Fetch data error:", {
        message: err.message,
        response: err.response?.data,
        status: err.response?.status,
      });
      setError(
        `Failed to load data: ${err.message}${
          err.response?.status ? ` (HTTP ${err.response.status})` : ""
        }`
      );
    } finally {
      setIsLoading(false);
    }
  };

  const fetchSalaries = async () => {
    try {
      console.log("Fetching salaries from http://localhost:5000/salary");
      const response = await axios.get("http://localhost:5000/salary", {
        timeout: 5000,
      });
      console.log("Salaries received:", response.data);
      const sanitizedSalaries = (response.data || []).map(normalizeSalary);
      setSalaries(sanitizedSalaries);
    } catch (err) {
      console.error("Salaries fetch failed:", err.message);
      setSalaries([]);
      throw err;
    }
  };

  const fetchJobs = async () => {
    try {
      console.log("Fetching jobs from http://localhost:5000/job");
      const response = await axios.get("http://localhost:5000/job", {
        timeout: 5000,
      });
      console.log("Jobs received:", response.data);
      setJobs(response.data || []);
    } catch (err) {
      console.error("Jobs fetch failed:", err.message);
      setJobs([]);
      throw err;
    }
  };

  // Filter and slice salaries
  const filteredSalaries = salaries.filter((salary) =>
    `${salary.Job_Title} ${salary.position}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );
  const displayedSalaries = filteredSalaries.slice(0, entriesToShow);

  // PDF Generation
  const handleViewPDF = async () => {
    try {
      const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
      window.open(URL.createObjectURL(blob));
    } catch (err) {
      setError("Failed to generate PDF: " + err.message);
    }
  };

  // Editing Handlers
  const startEditing = (salary) => {
    const job = jobs.find((j) => j.Job_Title === salary.Job_Title);
    setEditingId(salary.id);
    setEditedSalary({
      job_id: job?.id || "",
      position: salary.position || "",
      salary: salary.salary || "",
    });
  };

  const handleEditChange = (e, field) => {
    const value =
      field === "job_id" ? parseInt(e.target.value) : e.target.value;
    setEditedSalary((prev) => ({ ...prev, [field]: value }));
  };

  const saveSalary = async (id) => {
    setIsLoading(true);
    setError(null);
    const salaryValue = parseFloat(editedSalary.salary);
    if (
      !editedSalary.job_id ||
      !editedSalary.position?.trim() ||
      isNaN(salaryValue) ||
      salaryValue < 0
    ) {
      setError("Please fill all fields with valid data");
      setIsLoading(false);
      return;
    }

    try {
      await axios.patch(
        `http://localhost:5000/salary/${id}`,
        {
          job_id: parseInt(editedSalary.job_id),
          position: editedSalary.position.trim(),
          salary: salaryValue,
        },
        { headers: { "Content-Type": "application/json" } }
      );
      await fetchSalaries();
      setEditingId(null);
      setEditedSalary({});
      alert("Salary updated successfully!");
    } catch (err) {
      setError(
        `Failed to update salary: ${err.response?.data?.message || err.message}`
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Adding Handlers
  const handleNewSalaryChange = (e, field) => {
    const value =
      field === "job_id" ? parseInt(e.target.value) : e.target.value;
    setNewSalary((prev) => ({ ...prev, [field]: value }));
  };

  const saveNewSalary = async () => {
    setIsLoading(true);
    setError(null);
    const salaryValue = parseFloat(newSalary.salary);
    if (
      !newSalary.job_id ||
      !newSalary.position?.trim() ||
      isNaN(salaryValue) ||
      salaryValue <= 0
    ) {
      setError("Please fill all fields with valid data");
      setIsLoading(false);
      return;
    }

    try {
      await axios.post(
        "http://localhost:5000/salary",
        {
          job_id: parseInt(newSalary.job_id),
          position: newSalary.position.trim(),
          salary: salaryValue,
        },
        { headers: { "Content-Type": "application/json" } }
      );
      await fetchSalaries();
      setNewSalary({ job_id: "", position: "", salary: "" });
      setIsAdding(false);
      alert("Salary added successfully!");
    } catch (err) {
      setError(
        `Failed to add salary: ${err.response?.data?.message || err.message}`
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Render
  return (
    <div style={{ position: "relative" }}>
      {/* Background */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      {/* Main Content */}
      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        {isLoading && <div className="notification is-info">Loading...</div>}
        {error && (
          <div className="notification is-danger">
            {error}
            <button
              className="button is-small is-light ml-2"
              onClick={fetchData}
              disabled={isLoading}
            >
              Retry
            </button>
          </div>
        )}

        {/* Search and Actions */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input mt-2"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            disabled={isLoading}
          />
          <div className="mt-2 is-flex">
            {!isMobile && !isAdding && (
              <button
                className="button is-success ml-3"
                onClick={() => setIsAdding(true)}
                disabled={isLoading}
              >
                Add Position
              </button>
            )}
            {isMobile && (
              <Link
                to="/home/AddSalary"
                className="button is-success ml-3"
                disabled={isLoading}
              >
                Add Position
              </Link>
            )}
            <button
              onClick={handleViewPDF}
              className="button is-info ml-3"
              disabled={isLoading}
            >
              View PDF
            </button>
          </div>
        </div>

        {/* Desktop Table */}
        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Salary ID</th>
                <th>Job Title</th>
                <th>Position / Level</th>
                <th>Salary</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {isAdding && (
                <tr>
                  <td>New</td>
                  <td>
                    <div className="select is-fullwidth">
                      <select
                        value={newSalary.job_id}
                        onChange={(e) => handleNewSalaryChange(e, "job_id")}
                        disabled={isLoading}
                      >
                        <option value="">Select a job</option>
                        {jobs.map((job) => (
                          <option key={job.id} value={job.id}>
                            {job.Job_Title}
                          </option>
                        ))}
                      </select>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newSalary.position}
                      onChange={(e) => handleNewSalaryChange(e, "position")}
                      placeholder="Enter position"
                      disabled={isLoading}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="input"
                      value={newSalary.salary}
                      onChange={(e) => handleNewSalaryChange(e, "salary")}
                      placeholder="Enter salary"
                      min="0"
                      step="0.01"
                      disabled={isLoading}
                    />
                  </td>
                  <td>
                    <button
                      className="button is-small is-success mr-2"
                      onClick={saveNewSalary}
                      disabled={isLoading}
                    >
                      Save
                    </button>
                    <button
                      className="button is-small is-danger"
                      onClick={() => setIsAdding(false)}
                      disabled={isLoading}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              )}
              {displayedSalaries.map((salary) => (
                <tr key={salary.id}>
                  <td>{salary.id}</td>
                  <td>
                    {editingId === salary.id ? (
                      <div className="select is-fullwidth">
                        <select
                          value={editedSalary.job_id || ""}
                          onChange={(e) => handleEditChange(e, "job_id")}
                          disabled={isLoading}
                        >
                          <option value="">Select a job</option>
                          {jobs.map((job) => (
                            <option key={job.id} value={job.id}>
                              {job.Job_Title}
                            </option>
                          ))}
                        </select>
                      </div>
                    ) : (
                      salary.Job_Title
                    )}
                  </td>
                  <td>
                    {editingId === salary.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedSalary.position || ""}
                        onChange={(e) => handleEditChange(e, "position")}
                        disabled={isLoading}
                      />
                    ) : (
                      salary.position
                    )}
                  </td>
                  <td>
                    {editingId === salary.id ? (
                      <input
                        type="number"
                        className="input"
                        value={editedSalary.salary || ""}
                        onChange={(e) => handleEditChange(e, "salary")}
                        step="0.01"
                        disabled={isLoading}
                      />
                    ) : (
                      `₱${parseFloat(salary.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })}`
                    )}
                  </td>
                  <td>
                    {editingId === salary.id ? (
                      <>
                        <button
                          className="button is-small is-success mr-2"
                          onClick={() => saveSalary(salary.id)}
                          disabled={isLoading}
                        >
                          Save
                        </button>
                        <button
                          className="button is-small is-danger"
                          onClick={() => setEditingId(null)}
                          disabled={isLoading}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="button is-small is-info"
                        onClick={() => startEditing(salary)}
                        disabled={isLoading}
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          // Mobile View
          <div className="columns is-multiline">
            {displayedSalaries.map((salary) => (
              <div key={salary.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{salary.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Salary ID:</strong> {salary.id}
                    </p>
                    <p>
                      <strong>Position:</strong> {salary.position}
                    </p>
                    <p>
                      <strong>Salary:</strong>{" "}
                      {`₱${parseFloat(salary.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })}`}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/editSalary/${salary.id}`}
                      className="card-footer-item button is-small is-info"
                      disabled={isLoading}
                    >
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
                disabled={isLoading}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredSalaries.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedSalaries.length} of {filteredSalaries.length}{" "}
            entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default SalaryList;
