import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false);
  const [editRow, setEditRow] = useState(null);
  const [editedData, setEditedData] = useState({});
  const [positions, setPositions] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [selectedDepartment, setSelectedDepartment] = useState(null);

  useEffect(() => {
    getUsers();
    fetchDepartments();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    if (selectedDepartment) {
      fetchPositions(selectedDepartment);
    }
  }, [selectedDepartment]);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDepartments(response.data);
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchPositions = async (dep_id) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/salary/getPosition/${dep_id}`
      );
      setPositions(response.data);
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const handleEdit = (user) => {
    setEditRow(user.id);
    setSelectedDepartment(user.job_id); // Set selected department for position fetching
    setEditedData({
      full_name: user.full_name,
      email: user.email,
      job_id: user.job_id,
      position_id: user.position_id,
      salary: user.salary,
    });
  };

  const handleEditChange = (e, field) => {
    const value = e.target.value;
    setEditedData((prev) => ({ ...prev, [field]: value }));

    // If Job Title changes, update positions
    if (field === "job_id") {
      setSelectedDepartment(value);
    }
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:5000/users/${id}`, editedData);
      setEditRow(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  return (
    <div style={{ position: "relative" }}>
      <div className="column container mt-5">
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="is-flex">
            {isMobile ? (
              <Link to="/home/AddEmployee" className="button is-success mr-1">
                Add Employee
              </Link>
            ) : (
              <button
                className="button is-success ml-3"
                onClick={() => setIsModalActive(true)}
              >
                Add Job Title
              </button>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {displayedUsers.length > 0 ? (
                displayedUsers.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>
                      {editRow === user.id ? (
                        <input
                          type="text"
                          className="input"
                          value={editedData.full_name}
                          onChange={(e) => handleEditChange(e, "full_name")}
                        />
                      ) : (
                        user.full_name
                      )}
                    </td>
                    <td>{user.email}</td>
                    <td>{user.Job_Title}</td>
                    <td>{user.position}</td>
                    <td>â‚±{user.salary?.toLocaleString("en-PH") || "-"}</td>
                    <td>
                      {editRow === user.id ? (
                        <button className="button is-success" onClick={() => handleSave(user.id)}>
                          Save
                        </button>
                      ) : (
                        <button className="button is-info" onClick={() => handleEdit(user)}>
                          Edit
                        </button>
                      )}
                      <button className="button is-danger" onClick={() => openDelete(user)}>
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="7">No users found.</td>
                </tr>
              )}
            </tbody>
          </table>
        ) : null}
      </div>

      <ConfirmBox open={open} closeDialog={() => setOpen(false)} deleteFunction={deleteUser} />
      <AddEmployeeModal isOpen={isModalActive} onClose={() => setIsModalActive(false)} />
    </div>
  );
};

export default UserList;
