import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditUser = () => {
  const [full_name, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("");
  const [departments, setDepartments] = useState([]);
  const [dep_id, setDepId] = useState("");
  const [positions, setPositions] = useState([]);
  const [position_id, setPositionId] = useState("");
  const [salary, setSalary] = useState("");

  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    fetchUserData();
    fetchDepartments();
  }, [id]);

  useEffect(() => {
    if (dep_id) {
      fetchPositions(dep_id);
    } else {
      setPositions([]);
      setPositionId("");
      setSalary("");
    }
  }, [dep_id]);

  useEffect(() => {
    const selectedPosition = positions.find((pos) => String(pos.id) === String(position_id));
    setSalary(selectedPosition ? selectedPosition.salary : "");
  }, [position_id, positions]);

  // Fetch User Data
  const fetchUserData = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/users/${id}`);
      const user = response.data;
      setFullName(user.full_name);
      setEmail(user.email);
      setGender(user.gender);
      setDepId(user.job_id);
      setPositionId(user.salary_id);
    } catch (error) {
      console.error("Error fetching user:", error);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/departments");
      setDepartments(response.data);
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchPositions = async (departmentId) => {
    try {
      const response = await axios.get(`http://localhost:5000/salary/getPosition/${departmentId}`);
      setPositions(response.data);
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const updateUser = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`http://localhost:5000/users/${id}`, {
        full_name,
        email,
        gender,
        job_id: dep_id,
        salary_id: position_id,
      });
      navigate("/home/Employee");
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="column is-half container mt-5">
      <form onSubmit={updateUser}>
        <div className="field">
          <label className="label">Full Name</label>
          <div className="control">
            <input
              type="text"
              className="input"
              value={full_name}
              onChange={(e) => setFullName(e.target.value)}
              placeholder="Enter full name"
              required
            />
          </div>
        </div>

        <div className="field">
          <label className="label">Email</label>
          <div className="control">
            <input
              type="email"
              className="input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
            />
          </div>
        </div>

        <div className="field">
          <label className="label">Gender</label>
          <div className="control">
            <div className="select is-fullwidth">
              <select value={gender} onChange={(e) => setGender(e.target.value)} required>
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
              </select>
            </div>
          </div>
        </div>

        <div className="field">
          <label className="label">Job Title</label>
          <div className="control">
            <div className="select is-fullwidth">
              <select value={dep_id} onChange={(e) => setDepId(e.target.value)} required>
                <option value="">Select Job Title</option>
                {departments.map((dep) => (
                  <option key={dep.id} value={dep.id}>
                    {dep.Job_Title}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        <div className="field">
          <label className="label">Position</label>
          <div className="control">
            <div className="select is-fullwidth">
              <select
                value={position_id}
                onChange={(e) => setPositionId(e.target.value)}
                required
              >
                <option value="">Select Position</option>
                {positions.map((pos) => (
                  <option key={pos.id} value={pos.id}>
                    {pos.position}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        <div className="field">
          <label className="label">Salary</label>
          <div className="control">
            <input
              type="text"
              className="input"
              value={
                salary
                  ? `₱${parseFloat(salary).toLocaleString("en-PH", {
                      minimumFractionDigits: 2,
                    })}`
                  : ""
              }
              disabled
            />
          </div>
        </div>

        <div className="field">
          <button type="submit" className="button is-success">
            Update
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditUser;

