import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";
import "../styles/UserList.css"; // Ensure this file contains styling similar to JobList

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false);
  const [editRow, setEditRow] = useState(null);
  const [editedData, setEditedData] = useState({});
  const [positions, setPositions] = useState([]);
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    getUsers();
    fetchDepartments();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDepartments(response.data);
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchPositions = async (dep_id) => {
    try {
      const response = await axios.get(`http://localhost:5000/salary/getPosition/${dep_id}`);
      setPositions(response.data);
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const handleEdit = (user) => {
    setEditRow(user.id);
    setEditedData({ ...user });
    fetchPositions(user.job_id);
  };

  const handleInputChange = (e, field) => {
    const value = e.target.value;
    setEditedData((prevData) => ({
      ...prevData,
      [field]: value,
    }));

    if (field === "job_id") {
      fetchPositions(value);
      setEditedData((prevData) => ({
        ...prevData,
        position_id: "",
        salary: "",
      }));
    }

    if (field === "position_id") {
      const selectedPosition = positions.find((pos) => String(pos.id) === String(value));
      setEditedData((prevData) => ({
        ...prevData,
        salary: selectedPosition ? selectedPosition.salary : "",
      }));
    }
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:5000/users/${id}`, editedData);
      setEditRow(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="userlist-container">
      <div className="table-container overflow-x-auto">
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>Employee ID</th>
              <th>Full Name</th>
              <th>Email</th>
              <th>Job Title</th>
              <th>Position</th>
              <th>Salary</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.slice(0, entriesToShow).map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>
                  {editRow === user.id ? (
                    <input
                      type="text"
                      value={editedData.full_name}
                      onChange={(e) => handleInputChange(e, "full_name")}
                    />
                  ) : (
                    user.full_name
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <input
                      type="text"
                      value={editedData.email}
                      onChange={(e) => handleInputChange(e, "email")}
                    />
                  ) : (
                    user.email
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <select value={editedData.job_id} onChange={(e) => handleInputChange(e, "job_id")}>
                      <option value="">Select Job Title</option>
                      {departments.map((dep) => (
                        <option key={dep.id} value={dep.id}>
                          {dep.Job_Title}
                        </option>
                      ))}
                    </select>
                  ) : (
                    user.Job_Title
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <select value={editedData.position_id} onChange={(e) => handleInputChange(e, "position_id")}>
                      <option value="">Select Position</option>
                      {positions.map((pos) => (
                        <option key={pos.id} value={pos.id}>
                          {pos.position}
                        </option>
                      ))}
                    </select>
                  ) : (
                    user.position
                  )}
                </td>
                <td>
                  {editRow === user.id ? (
                    <input
                      type="text"
                      value={editedData.salary ? `₱${editedData.salary.toLocaleString("en-PH", { minimumFractionDigits: 2 })}` : ""}
                      disabled
                    />
                  ) : (
                    `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                  )}
                </td>
                <td>
                  <button
                    onClick={() => (editRow === user.id ? handleSave(user.id) : handleEdit(user))}
                    className="button is-small is-info"
                  >
                    {editRow === user.id ? "Save" : "Edit"}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default UserList;
