import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false);
  const [editRow, setEditRow] = useState(null);
  const [editedData, setEditedData] = useState({});
  const [positions, setPositions] = useState([]);
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    getUsers();
    fetchDepartments();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDepartments(response.data);
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchPositions = async (dep_id) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/salary/getPosition/${dep_id}`
      );
      setPositions(response.data);
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const handleEdit = (user) => {
    setEditRow(user.id);
    setEditedData({ ...user });
    fetchPositions(user.job_id);
  };

  const handleInputChange = (e, field) => {
    const value = e.target.value;
    setEditedData((prevData) => ({
      ...prevData,
      [field]: value,
    }));

    if (field === "job_id") {
      fetchPositions(value);
      setEditedData((prevData) => ({
        ...prevData,
        position_id: "",
        salary: "",
      }));
    }

    if (field === "position_id") {
      const selectedPosition = positions.find(
        (pos) => String(pos.id) === String(value)
      );
      setEditedData((prevData) => ({
        ...prevData,
        salary: selectedPosition ? selectedPosition.salary : "",
      }));
    }
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:5000/users/${id}`, editedData);
      setEditRow(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div style={{ position: "relative" }}>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      {/* Dark Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />
      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="is-flex">
            {isMobile ? (
              <Link to="/home/AddEmployee" className="button is-success mr-1">
                Add Employee
              </Link>
            ) : (
              <div>
                <button
                  className="button is-success ml-3"
                  onClick={() => setIsModalActive(true)} // Open modal
                >
                  Add Job Title
                </button>{" "}
                <AddEmployeeModal
                  isOpen={isModalActive}
                  onClose={() => setIsModalActive(false)}
                />
              </div>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        {!isMobile ? (
          <div className="table-container overflow-x-auto">
            <table className="table is-striped is-fullwidth">
              <thead>
                <tr>
                  <th>Employee ID</th>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Job Title</th>
                  <th>Position</th>
                  <th>Salary</th>
                  <th>Annual Salary</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {displayedUsers.length > 0 ? (
                  displayedUsers.map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            className="input"
                            value={editedData.full_name}
                            onChange={(e) => handleInputChange(e, "full_name")}
                          />
                        ) : (
                          user.full_name
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            className="input"
                            value={editedData.email}
                            onChange={(e) => handleInputChange(e, "email")}
                          />
                        ) : (
                          user.email
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <div className="select is-fullwidth">
                            <select
                              value={editedData.job_id}
                              onChange={(e) => handleInputChange(e, "job_id")}
                            >
                              <option value="">Select Job Title</option>
                              {departments.map((dep) => (
                                <option key={dep.id} value={dep.id}>
                                  {dep.Job_Title}
                                </option>
                              ))}
                            </select>
                          </div>
                        ) : (
                          user.Job_Title
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <div className="select is-fullwidth">
                            <select
                              value={editedData.position_id}
                              onChange={(e) =>
                                handleInputChange(e, "position_id")
                              }
                            >
                              <option value="">Select Position</option>
                              {positions.map((pos) => (
                                <option key={pos.id} value={pos.id}>
                                  {pos.position}
                                </option>
                              ))}
                            </select>
                          </div>
                        ) : (
                          user.position
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            className="input"
                            value={
                              editedData.salary
                                ? `₱${editedData.salary.toLocaleString(
                                    "en-PH",
                                    { minimumFractionDigits: 2 }
                                  )}`
                                : ""
                            }
                            disabled
                          />
                        ) : (
                          `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                        )}
                      </td>
                      <td>
                         `₱{user.anual_salary?.toLocaleString("en-PH") || "-"}`
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <>
                            <button
                              className="button is-small is-success mr-2"
                              onClick={() => handleSave(user.id)}
                            >
                              Save
                            </button>
                            <button
                              className="button is-small is-danger"
                              onClick={() => setEditRow(null)} // Cancel edit
                            >
                              Cancel
                            </button>
                          </>
                        ) : (
                          <div>
                            <button
                              onClick={() =>
                                editRow === user.id
                                  ? handleSave(user.id)
                                  : handleEdit(user)
                              }
                              className="button is-small is-info mr-3"
                            >
                              {editRow === user.id ? "Save" : "Edit"}
                            </button>
                            <button
                              onClick={() => openDelete(user)}
                              className="button is-small is-danger"
                            >
                              Delete
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="8" className="has-text-centered">
                      No users found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="columns is-multiline m-2">
            {displayedUsers.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{user.full_name}</p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Employee ID:</strong> {user.id}
                    </p>
                    <p>
                      <strong>Email:</strong> {user.email}
                    </p>
                    <p>
                      <strong>Job Title:</strong> {user.Job_Title}
                    </p>
                    <p>
                      <strong>Position:</strong> {user.position}
                    </p>
                    <p>
                      <strong>Salary:</strong> ₱
                      {user.salary?.toLocaleString("en-PH") || "-"}
                    </p>
                    <p>
                      <strong>Annual Salary:</strong> ₱
                      {user.anual_salary?.toLocaleString("en-PH") || "-"}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/edit/${user.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                    <button
                      onClick={() => openDelete(user)}
                      className="card-footer-item button is-small is-danger"
                    >
                      Delete
                    </button>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 p-3 has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredUsers.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedUsers.length} of {filteredUsers.length} entries
          </p>
        </div>

        <ConfirmBox
          open={open}
          closeDialog={() => setOpen(false)}
          title={deleteData?.full_name}
          empID={deleteData?.id}
          deleteFunction={deleteUser}
        />
      </div>
    </div>
  );
};

