import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingRow, setEditingRow] = useState(null);
  const [editedUser, setEditedUser] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleEditClick = (user) => {
    setEditingRow(user.id);
    setEditedUser({ ...user });
  };

  const handleChange = (e) => {
    setEditedUser({ ...editedUser, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/users/${editedUser.id}`, editedUser);
      getUsers();
      setEditingRow(null);
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div>
      {!isMobile ? (
        <div className="table-container overflow-x-auto">
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Annual Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.slice(0, entriesToShow).map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.full_name}</td>
                  <td>{user.email}</td>
                  <td>
                    {editingRow === user.id ? (
                      <input type="text" name="Job_Title" value={editedUser.Job_Title} onChange={handleChange} className="input" />
                    ) : (
                      user.Job_Title
                    )}
                  </td>
                  <td>
                    {editingRow === user.id ? (
                      <input type="text" name="position" value={editedUser.position} onChange={handleChange} className="input" />
                    ) : (
                      user.position
                    )}
                  </td>
                  <td>₱{user.salary?.toLocaleString("en-PH") || "-"}</td>
                  <td>₱{user.anual_salary?.toLocaleString("en-PH") || "-"}</td>
                  <td>
                    {editingRow === user.id ? (
                      <button onClick={handleSave} className="button is-small is-success mr-2">Save</button>
                    ) : (
                      <button onClick={() => handleEditClick(user)} className="button is-small is-warning mr-2">Edit</button>
                    )}
                    <button onClick={() => openDelete(user)} className="button is-small is-danger">Delete</button>
                  </td>
                </tr>
              ))}
              {users.length === 0 && (
                <tr>
                  <td colSpan="8" className="has-text-centered">No users found.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="columns is-multiline m-2">
          {users.map((user) => (
            <div key={user.id} className="column is-12">
              <div className="card">
                <header className="card-header">
                  <p className="card-header-title">{user.full_name}</p>
                </header>
                <div className="card-content">
                  <p><strong>Employee ID:</strong> {user.id}</p>
                  <p><strong>Email:</strong> {user.email}</p>
                  <p><strong>Job Title:</strong> {user.Job_Title}</p>
                  <p><strong>Position:</strong> {user.position}</p>
                  <p><strong>Salary:</strong> ₱{user.salary?.toLocaleString("en-PH") || "-"}</p>
                  <p><strong>Annual Salary:</strong> ₱{user.anual_salary?.toLocaleString("en-PH") || "-"}</p>
                </div>
                <footer className="card-footer">
                  <button onClick={() => handleEditClick(user)} className="card-footer-item button is-small is-warning">Edit</button>
                  <button onClick={() => openDelete(user)} className="card-footer-item button is-small is-danger">Delete</button>
                </footer>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserList;

