import React, { useState, useEffect } from "react";
import axios from "axios";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  const [salaries, setSalaries] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  const [newSalary, setNewSalary] = useState(null);

  useEffect(() => {
    fetchSalaries();
    fetchJobs();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const fetchSalaries = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      setSalaries(response.data);
    } catch (error) {
      console.error("Error fetching salaries:", error);
    }
  };

  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching jobs:", error);
    }
  };

  const handleAddRow = () => {
    setNewSalary({ job_id: "", position: "", salary: "" });
  };

  const handleEditChange = (e, field, isNew = false) => {
    const value = e.target.value;
    if (isNew) {
      setNewSalary((prev) => ({ ...prev, [field]: value }));
    } else {
      setEditedSalary((prev) => ({ ...prev, [field]: value }));
    }
  };

  const saveSalary = async (id, isNew = false) => {
    try {
      if (isNew) {
        await axios.post("http://localhost:5000/salary", newSalary);
        setNewSalary(null);
      } else {
        await axios.patch(`http://localhost:5000/salary/${id}`, editedSalary);
        setEditingId(null);
      }
      fetchSalaries();
    } catch (error) {
      console.error("Error saving salary:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url, "_blank");
  };

  return (
    <div className="container mt-5">
      <div className="is-flex is-justify-content-space-between mb-3">
        <input
          type="text"
          className="input"
          placeholder="Search by Job Title or Position..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="button is-success" onClick={handleAddRow}>
          Add Job Salary
        </button>
        <button className="button is-info" onClick={handleViewPDF}>
          View PDF
        </button>
      </div>

      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Job Title</th>
            <th>Position / Level</th>
            <th>Salary</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {newSalary && (
            <tr>
              <td>
                <select
                  className="select is-fullwidth"
                  value={newSalary.job_id}
                  onChange={(e) => handleEditChange(e, "job_id", true)}
                >
                  <option value="">Select Job</option>
                  {jobs.map((job) => (
                    <option key={job.id} value={job.id}>
                      {job.Job_Title}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <input
                  type="text"
                  className="input"
                  value={newSalary.position}
                  onChange={(e) => handleEditChange(e, "position", true)}
                />
              </td>
              <td>
                <input
                  type="number"
                  className="input"
                  value={newSalary.salary}
                  onChange={(e) => handleEditChange(e, "salary", true)}
                />
              </td>
              <td>
                <button className="button is-success" onClick={() => saveSalary(null, true)}>
                  Save
                </button>
              </td>
            </tr>
          )}

          {salaries.map((user) => (
            <tr key={user.id}>
              <td>{user.Job_Title}</td>
              <td>{user.position}</td>
              <td>{`₱${parseFloat(user.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}`}</td>
              <td>
                <button
                  className="button is-small is-info"
                  onClick={() => setEditingId(user.id)}
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default SalaryList;
