import React, { useState } from "react";
import { motion } from "framer-motion";

const Resume = () => {
  const [darkMode, setDarkMode] = useState(false);

  return (
    <div
      className={`box mt-5 ${darkMode ? "has-background-black has-text-light" : "has-background-white has-text-dark"}`}
      style={{
        maxWidth: "800px",
        margin: "auto",
        padding: "2rem",
        borderRadius: "12px",
        boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
        transition: "background 0.5s, color 0.5s",
      }}
    >
      {/* Dark Mode Toggle */}
      <div className="has-text-right">
        <button
          className={`button ${darkMode ? "is-light" : "is-dark"}`}
          onClick={() => setDarkMode(!darkMode)}
        >
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </div>

      {/* Profile Image with Animation */}
      <motion.div
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.6 }}
        style={{ textAlign: "center", marginBottom: "1rem" }}
      >
        <img
          src="/481827451_608546738820226_3573643982633982007_n.jpg"
          alt="Ryan Santiago"
          style={{
            borderRadius: "50%",
            width: "128px",
            height: "128px",
            objectFit: "cover",
            border: "3px solid #ccc",
          }}
        />
      </motion.div>

      {/* Header */}
      <motion.h2
        className={`title is-4 has-text-centered ${darkMode ? "has-text-white" : "has-text-dark"}`}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        Ryan C. Santiago
      </motion.h2>
      <motion.p
        className={`subtitle is-6 has-text-centered ${darkMode ? "has-text-grey-light" : "has-text-grey-dark"}`}
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Programmer & IT Staff
      </motion.p>

      <hr className={darkMode ? "has-background-grey-light" : "has-background-grey-dark"} />

      {/* Contact Information */}
      <motion.div
        className={`content ${darkMode ? "has-text-white" : "has-text-dark"}`}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
      >
        <h3 className="title is-5">Contact</h3>
        <p><strong>Location:</strong> Taal, Pulilan, Bulacan</p>
        <p><strong>Phone:</strong> 0926-695-5822</p>
        <p><strong>Email:</strong> santiagoryan788@gmail.com</p>
        <p>
          <strong>GitHub:</strong>{" "}
          <a
            href="https://github.com/Ryalynne"
            target="_blank"
            rel="noopener noreferrer"
            className={darkMode ? "has-text-info-light" : "has-text-info-dark"}
          >
            github.com/Ryalynne
          </a>
        </p>
      </motion.div>

      {/* Experience */}
      <motion.div initial={{ opacity: 0, x: -30 }} animate={{ opacity: 1, x: 0 }} transition={{ duration: 0.5 }}>
        <h3 className="title is-5">Experience</h3>
        <p><strong>IT Programmer & IT Staff</strong> - Baliwag Maritime Academy (Sept 2023 – Feb 2024)</p>
        <ul>
          <li>Developed and maintained BMA Portal, Library System, and website.</li>
          <li>Provided technical support for software and hardware issues.</li>
        </ul>
        <p><strong>Part-time Professor</strong> - STI Baliuag (Sept 2023 – June 2024)</p>
        <ul>
          <li>Taught Game Development, Advanced Database, and Network Technology.</li>
        </ul>
      </motion.div>

      {/* Education */}
      <motion.div initial={{ opacity: 0, x: 30 }} animate={{ opacity: 1, x: 0 }} transition={{ duration: 0.6 }}>
        <h3 className="title is-5">Education</h3>
        <p><strong>Bachelor of Science in Information Technology</strong> - STI College Baliuag (2019 – 2023)</p>
      </motion.div>

      {/* Skills */}
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.7 }}>
        <h3 className="title is-5">Skills</h3>
        <div className="tags">
          <span className={`tag ${darkMode ? "is-light" : "is-primary"}`}>Laravel</span>
          <span className={`tag ${darkMode ? "is-light" : "is-info"}`}>.NET</span>
          <span className={`tag ${darkMode ? "is-light" : "is-success"}`}>React</span>
          <span className={`tag ${darkMode ? "is-light" : "is-warning"}`}>Next.js</span>
          <span className={`tag ${darkMode ? "is-light" : "is-danger"}`}>SQL</span>
          <span className={`tag ${darkMode ? "is-light" : "is-link"}`}>PHP</span>
        </div>
      </motion.div>

      {/* Certifications */}
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.8 }}>
        <h3 className="title is-5">Certifications</h3>
        <ul>
          <li>SAP Business One Advance (Implementation and Support) - March 2021</li>
          <li>One STI Educators Training (Onset) Starter - September 2023</li>
        </ul>
      </motion.div>
    </div>
  );
};

export default Resume;
