import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  // State Hooks
  const [salaries, setSalaries] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  const [jobs, setJobs] = useState([]);
  const [newSalary, setNewSalary] = useState({
    job_id: "",
    position: "",
    salary: "",
  });
  const [isAdding, setIsAdding] = useState(false);

  // Normalize salary data to handle nulls consistently
  const normalizeSalary = (salary) => ({
    id: salary.id || null,
    Job_Title: salary.Job_Title || "Untitled",
    position: salary.position || "N/A",
    salary: salary.salary || "0",
  });

  // Effect Hook for fetching data and handling resize
  useEffect(() => {
    fetchSalaries();
    fetchJobs();

    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Fetch salary data from API
  const fetchSalaries = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      console.log("Fetched salaries:", response.data);
      const sanitizedSalaries = (response.data || []).map(normalizeSalary);
      setSalaries(sanitizedSalaries);
    } catch (error) {
      console.error("Error fetching salaries:", error);
      setSalaries([]);
    }
  };

  // Fetch job titles from API
  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      console.log("Fetched jobs:", response.data);
      setJobs(response.data || []);
    } catch (error) {
      console.error("Error fetching job titles:", error);
      setJobs([]);
    }
  };

  // Filter and slice salaries for display
  const filteredSalaries = salaries.filter((salary) =>
    `${salary.Job_Title} ${salary.position}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );
  const displayedSalaries = filteredSalaries.slice(0, entriesToShow);

  // Generate and open salary list PDF
  const handleViewPDF = async () => {
    const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  // Start inline editing for a salary record
  const startEditing = (salary) => {
    console.log("Editing Salary:", salary);
    const job = jobs.find((j) => j.Job_Title === salary.Job_Title);
    setEditingId(salary.id);
    setEditedSalary({
      job_id: job ? job.id : "",
      position: salary.position || "",
      salary: salary.salary || "",
    });
  };

  // Cancel editing
  const cancelEditing = () => {
    setEditingId(null);
    setEditedSalary({});
  };

  // Handle input changes in edit form
  const handleEditChange = (e, field) => {
    const value = e.target.value;
    console.log(`Edit Field: ${field}, Value: ${value}`);
    setEditedSalary((prev) => ({ ...prev, [field]: value }));
  };

  // Save updated salary record
  const saveSalary = async (id) => {
    const updatedSalary = {
      depId: editedSalary.job_id || null, // Match API key
      position: editedSalary.position || "N/A",
      salary: editedSalary.salary || "0",
    };

    if (!updatedSalary.depId) {
      alert("Job Title is required");
      return;
    }

    console.log("Saving Salary Payload:", updatedSalary);
    try {
      await axios.patch(`http://localhost:5000/salary/${id}`, updatedSalary);
      await fetchSalaries();
      setEditingId(null);
      alert("Salary updated successfully!");
    } catch (error) {
      console.error("Error updating salary:", error);
      alert(
        "Failed to update salary: " +
          (error.response?.data?.message || error.message)
      );
    }
  };

  // Start adding a new salary row
  const startAdding = () => {
    setIsAdding(true);
    setNewSalary({ job_id: "", position: "", salary: "" });
  };

  // Cancel adding a new salary
  const cancelAdding = () => {
    setIsAdding(false);
    setNewSalary({ job_id: "", position: "", salary: "" });
  };

  // Handle input changes for new salary row
  const handleNewSalaryChange = (e, field) => {
    const value = e.target.value;
    console.log(`New Salary Field: ${field}, Value: ${value}`);
    setNewSalary((prev) => ({ ...prev, [field]: value }));
  };

  // Save new salary record
  const saveNewSalary = async () => {
    const salaryToSave = {
      depId: newSalary.job_id || null,
      position: newSalary.position || "N/A",
      salary: newSalary.salary || "0",
    };

    if (!salaryToSave.depId || !salaryToSave.position || !salaryToSave.salary) {
      alert("All fields are required");
      return;
    }

    console.log("Saving New Salary Payload:", salaryToSave);
    try {
      await axios.post("http://localhost:5000/salary", salaryToSave);
      await fetchSalaries();
      setNewSalary({ job_id: "", position: "", salary: "" });
      setIsAdding(false); // Exit adding mode after save
      alert("Salary added successfully!");
    } catch (error) {
      console.error("Error adding salary:", error);
      alert(
        "Failed to add salary: " +
          (error.response?.data?.message || error.message)
      );
    }
  };

  return (
    <div style={{ position: "relative" }}>
      {/* Full-Screen Background */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      {/* Dark Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      {/* Main Content */}
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        {/* Search and Action Buttons */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input mt-2"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="mt-2 is-flex">
            {!isMobile && !isAdding && (
              <button className="button is-success ml-3" onClick={startAdding}>
                Add Position
              </button>
            )}
            {isMobile && (
              <Link to="/home/AddSalary" className="button is-success ml-3">
                Add Position
              </Link>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-3">
              View PDF
            </button>
          </div>
        </div>

        {/* Table View (Desktop) */}
        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Salary ID</th>
                <th>Job Title</th>
                <th>Position / Level</th>
                <th>Salary</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {isAdding && (
                <tr>
                  <td>New</td>
                  <td>
                    <div className="select is-fullwidth">
                      <select
                        value={newSalary.job_id}
                        onChange={(e) => handleNewSalaryChange(e, "job_id")}
                        required
                      >
                        <option value="">Select a job</option>
                        {jobs.map((job) => (
                          <option key={job.id} value={job.id}>
                            {job.Job_Title}
                          </option>
                        ))}
                      </select>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newSalary.position}
                      onChange={(e) => handleNewSalaryChange(e, "position")}
                      placeholder="Enter position"
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="input"
                      value={newSalary.salary}
                      onChange={(e) => handleNewSalaryChange(e, "salary")}
                      placeholder="Enter salary"
                    />
                  </td>
                  <td>
                    <button
                      className="button is-small is-success mr-2"
                      onClick={saveNewSalary}
                    >
                      Save
                    </button>
                    <button
                      className="button is-small is-danger"
                      onClick={cancelAdding}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              )}
              {displayedSalaries.map((salary) => (
                <tr key={salary.id}>
                  <td>{salary.id}</td>
                  <td>
                    {editingId === salary.id ? (
                      <div className="select is-fullwidth">
                        <select
                          value={editedSalary.job_id || ""}
                          onChange={(e) => handleEditChange(e, "job_id")}
                          required
                        >
                          <option value="">Select a job</option>
                          {jobs.map((job) => (
                            <option key={job.id} value={job.id}>
                              {job.Job_Title}
                            </option>
                          ))}
                        </select>
                      </div>
                    ) : (
                      salary.Job_Title
                    )}
                  </td>
                  <td>
                    {editingId === salary.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedSalary.position || ""}
                        onChange={(e) => handleEditChange(e, "position")}
                      />
                    ) : (
                      salary.position
                    )}
                  </td>
                  <td>
                    {editingId === salary.id ? (
                      <input
                        type="number"
                        className="input"
                        value={editedSalary.salary || ""}
                        onChange={(e) => handleEditChange(e, "salary")}
                      />
                    ) : (
                      `₱${parseFloat(salary.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })}`
                    )}
                  </td>
                  <td>
                    {editingId === salary.id ? (
                      <>
                        <button
                          className="button is-small is-success mr-2"
                          onClick={() => saveSalary(salary.id)}
                        >
                          Save
                        </button>
                        <button
                          className="button is-small is-danger"
                          onClick={cancelEditing}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="button is-small is-info"
                        onClick={() => startEditing(salary)}
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="columns is-multiline">
            {displayedSalaries.map((salary) => (
              <div key={salary.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{salary.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Salary ID:</strong> {salary.id}
                    </p>
                    <p>
                      <strong>Position:</strong> {salary.position}
                    </p>
                    <p>
                      <strong>Salary:</strong>{" "}
                      {`₱${parseFloat(salary.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })}`}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/editSalary/${salary.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer Controls */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredSalaries.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedSalaries.length} of {filteredSalaries.length}{" "}
            entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default SalaryList;
