import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false);
  const [editingUserId, setEditingUserId] = useState(null);
  const [editedUser, setEditedUser] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const handleEdit = (user) => {
    setEditingUserId(user.id);
    setEditedUser({ ...user });
  };

  const handleCancel = () => {
    setEditingUserId(null);
    setEditedUser({});
  };

  const handleChange = (e) => {
    setEditedUser({ ...editedUser, [e.target.name]: e.target.value });
  };

  const handleSave = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/users/${id}`, editedUser);
      setUsers(users.map((user) => (user.id === id ? editedUser : user)));
      setEditingUserId(null);
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      {/* Background & Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {!isMobile ? (
          <div className="table-container overflow-x-auto">
            <table className="table is-striped is-fullwidth">
              <thead>
                <tr>
                  <th>Employee ID</th>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Job Title</th>
                  <th>Position</th>
                  <th>Salary</th>
                  <th>Annual Salary</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.length > 0 ? (
                  users.slice(0, entriesToShow).map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>
                        {editingUserId === user.id ? (
                          <input
                            type="text"
                            name="full_name"
                            className="input is-small"
                            value={editedUser.full_name}
                            onChange={handleChange}
                          />
                        ) : (
                          user.full_name
                        )}
                      </td>
                      <td>
                        {editingUserId === user.id ? (
                          <input
                            type="email"
                            name="email"
                            className="input is-small"
                            value={editedUser.email}
                            onChange={handleChange}
                          />
                        ) : (
                          user.email
                        )}
                      </td>
                      <td>
                        {editingUserId === user.id ? (
                          <div className="select is-small">
                            <select
                              name="Job_Title"
                              value={editedUser.Job_Title}
                              onChange={handleChange}
                            >
                              <option value="Developer">Developer</option>
                              <option value="Designer">Designer</option>
                              <option value="Manager">Manager</option>
                            </select>
                          </div>
                        ) : (
                          user.Job_Title
                        )}
                      </td>
                      <td>
                        {editingUserId === user.id ? (
                          <div className="select is-small">
                            <select
                              name="position"
                              value={editedUser.position}
                              onChange={handleChange}
                            >
                              <option value="Junior">Junior</option>
                              <option value="Mid">Mid</option>
                              <option value="Senior">Senior</option>
                            </select>
                          </div>
                        ) : (
                          user.position
                        )}
                      </td>
                      <td>₱{user.salary?.toLocaleString("en-PH") || "-"}</td>
                      <td>₱{user.anual_salary?.toLocaleString("en-PH") || "-"}</td>
                      <td>
                        {editingUserId === user.id ? (
                          <>
                            <button
                              className="button is-small is-success mr-2"
                              onClick={() => handleSave(user.id)}
                            >
                              Save
                            </button>
                            <button className="button is-small is-warning" onClick={handleCancel}>
                              Cancel
                            </button>
                          </>
                        ) : (
                          <button
                            className="button is-small is-info mr-2"
                            onClick={() => handleEdit(user)}
                          >
                            Edit
                          </button>
                        )}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="8" className="has-text-centered">
                      No users found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        ) : (
          <p className="has-text-white">Mobile view remains unchanged.</p>
        )}
      </div>
    </div>
  );
};

export default UserList;
