import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false);
  const [editRow, setEditRow] = useState(null);
  const [editedData, setEditedData] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const jobTitles = ["Manager", "Developer", "Designer", "HR", "Sales"];
  const positions = ["Junior", "Mid", "Senior", "Lead"];

  const handleEdit = (user) => {
    setEditRow(user.id);
    setEditedData({ ...user });
  };

  const handleInputChange = (e, field) => {
    setEditedData({ ...editedData, [field]: e.target.value });
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:5000/users/${id}`, editedData);
      setEditRow(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="table-container overflow-x-auto">
      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Job Title</th>
            <th>Position</th>
            <th>Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {displayedUsers.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>
                {editRow === user.id ? (
                  <input
                    type="text"
                    value={editedData.full_name}
                    onChange={(e) => handleInputChange(e, "full_name")}
                  />
                ) : (
                  user.full_name
                )}
              </td>
              <td>
                {editRow === user.id ? (
                  <input
                    type="text"
                    value={editedData.email}
                    onChange={(e) => handleInputChange(e, "email")}
                  />
                ) : (
                  user.email
                )}
              </td>
              <td>
                {editRow === user.id ? (
                  <select
                    value={editedData.Job_Title}
                    onChange={(e) => handleInputChange(e, "Job_Title")}
                  >
                    {jobTitles.map((title) => (
                      <option key={title} value={title}>
                        {title}
                      </option>
                    ))}
                  </select>
                ) : (
                  user.Job_Title
                )}
              </td>
              <td>
                {editRow === user.id ? (
                  <select
                    value={editedData.position}
                    onChange={(e) => handleInputChange(e, "position")}
                  >
                    {positions.map((pos) => (
                      <option key={pos} value={pos}>
                        {pos}
                      </option>
                    ))}
                  </select>
                ) : (
                  user.position
                )}
              </td>
              <td>
                {editRow === user.id ? (
                  <input
                    type="number"
                    value={editedData.salary}
                    onChange={(e) => handleInputChange(e, "salary")}
                  />
                ) : (
                  `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                )}
              </td>
              <td>
                <button
                  onClick={() => (editRow === user.id ? handleSave(user.id) : handleEdit(user))}
                  className="button is-small is-info"
                >
                  {editRow === user.id ? "Save" : "Edit"}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
