import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isModalActive, setIsModalActive] = useState(false);
  const [editingUserId, setEditingUserId] = useState(null);
  const [editedUser, setEditedUser] = useState({});

  const jobTitles = ["Software Engineer", "Project Manager", "HR Manager"];
  const positions = ["Junior", "Mid", "Senior"];

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const startEditing = (user) => {
    setEditingUserId(user.id);
    setEditedUser({ ...user });
  };

  const cancelEditing = () => {
    setEditingUserId(null);
    setEditedUser({});
  };

  const saveUser = async () => {
    try {
      await axios.put(
        `http://localhost:5000/users/${editingUserId}`,
        editedUser
      );
      getUsers();
      setEditingUserId(null);
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  return (
    <div className="table-container overflow-x-auto">
      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Job Title</th>
            <th>Position</th>
            <th>Salary</th>
            <th>Annual Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {displayedUsers.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>
                {editingUserId === user.id ? (
                  <input
                    type="text"
                    className="input"
                    value={editedUser.full_name}
                    onChange={(e) =>
                      setEditedUser({ ...editedUser, full_name: e.target.value })
                    }
                  />
                ) : (
                  user.full_name
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <input
                    type="email"
                    className="input"
                    value={editedUser.email}
                    onChange={(e) =>
                      setEditedUser({ ...editedUser, email: e.target.value })
                    }
                  />
                ) : (
                  user.email
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <div className="select">
                    <select
                      value={editedUser.Job_Title}
                      onChange={(e) =>
                        setEditedUser({ ...editedUser, Job_Title: e.target.value })
                      }
                    >
                      {jobTitles.map((title) => (
                        <option key={title} value={title}>
                          {title}
                        </option>
                      ))}
                    </select>
                  </div>
                ) : (
                  user.Job_Title
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <div className="select">
                    <select
                      value={editedUser.position}
                      onChange={(e) =>
                        setEditedUser({ ...editedUser, position: e.target.value })
                      }
                    >
                      {positions.map((pos) => (
                        <option key={pos} value={pos}>
                          {pos}
                        </option>
                      ))}
                    </select>
                  </div>
                ) : (
                  user.position
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <input
                    type="number"
                    className="input"
                    value={editedUser.salary}
                    onChange={(e) =>
                      setEditedUser({ ...editedUser, salary: e.target.value })
                    }
                  />
                ) : (
                  `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <input
                    type="number"
                    className="input"
                    value={editedUser.anual_salary}
                    onChange={(e) =>
                      setEditedUser({ ...editedUser, anual_salary: e.target.value })
                    }
                  />
                ) : (
                  `₱${user.anual_salary?.toLocaleString("en-PH") || "-"}`
                )}
              </td>
              <td>
                {editingUserId === user.id ? (
                  <>
                    <button onClick={saveUser} className="button is-small is-success mr-2">
                      Save
                    </button>
                    <button onClick={cancelEditing} className="button is-small is-danger">
                      Cancel
                    </button>
                  </>
                ) : (
                  <>
                    <button
                      onClick={() => startEditing(user)}
                      className="button is-small is-info mr-2"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => openDelete(user)}
                      className="button is-small is-danger"
                    >
                      Delete
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
