import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingUserId, setEditingUserId] = useState(null);
  const [editedUser, setEditedUser] = useState({});
  const [departments, setDepartments] = useState([]);
  const [positions, setPositions] = useState([]);

  useEffect(() => {
    getUsers();
    fetchDepartments();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    if (editedUser.job_id) {
      fetchPositions(editedUser.job_id);
    }
  }, [editedUser.job_id]);

  useEffect(() => {
    const selectedPosition = positions.find(pos => String(pos.id) === String(editedUser.salary_id));
    setEditedUser(prev => ({ ...prev, salary: selectedPosition ? selectedPosition.salary : "" }));
  }, [editedUser.salary_id, positions]);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDepartments(response.data);
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchPositions = async (dep_id) => {
    try {
      const response = await axios.get(`http://localhost:5000/salary/getPosition/${dep_id}`);
      setPositions(response.data);
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const startEditing = (user) => {
    setEditingUserId(user.id);
    setEditedUser(user);
    fetchPositions(user.job_id);
  };

  const cancelEditing = () => {
    setEditingUserId(null);
    setEditedUser({});
  };

  const saveUser = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/users/${id}`, editedUser);
      setUsers(users.map(user => (user.id === id ? { ...user, ...editedUser } : user)));
      setEditingUserId(null);
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="table-container overflow-x-auto">
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>
                    {editingUserId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedUser.full_name || ""}
                        onChange={(e) => setEditedUser({ ...editedUser, full_name: e.target.value })}
                      />
                    ) : (
                      user.full_name
                    )}
                  </td>
                  <td>{user.email}</td>
                  <td>
                    {editingUserId === user.id ? (
                      <select
                        className="input"
                        value={editedUser.job_id || ""}
                        onChange={(e) => setEditedUser({ ...editedUser, job_id: e.target.value })}
                      >
                        {departments.map(dep => (
                          <option key={dep.id} value={dep.id}>{dep.Job_Title}</option>
                        ))}
                      </select>
                    ) : (
                      user.Job_Title
                    )}
                  </td>
                  <td>
                    {editingUserId === user.id ? (
                      <select
                        className="input"
                        value={editedUser.salary_id || ""}
                        onChange={(e) => setEditedUser({ ...editedUser, salary_id: e.target.value })}
                      >
                        {positions.map(pos => (
                          <option key={pos.id} value={pos.id}>{pos.position}</option>
                        ))}
                      </select>
                    ) : (
                      user.position
                    )}
                  </td>
                  <td>â‚±{editedUser.salary?.toLocaleString("en-PH") || "-"}</td>
                  <td>
                    {editingUserId === user.id ? (
                      <>
                        <button className="button is-small is-success mr-2" onClick={() => saveUser(user.id)}>Save</button>
                        <button className="button is-small is-danger" onClick={cancelEditing}>Cancel</button>
                      </>
                    ) : (
                      <button className="button is-small is-info mr-2" onClick={() => startEditing(user)}>Edit</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default UserList;
