import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  // State Hooks
  const [salaries, setSalary] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  const [jobs, setJobs] = useState([]);

  // Effect Hook for fetching data and handling window resize
  useEffect(() => {
    getUsers();
    getJobs();

    // Handle screen size changes
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);

    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Fetch salary data from API
  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      setSalary(response.data);
    } catch (error) {
      console.error("Error fetching salaries:", error);
    }
  };

  // Fetch job titles from API
  const getJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching job titles:", error);
    }
  };

  // Filter salaries based on search input
  const filteredSalaries = salaries.filter((user) =>
    `${user.Job_Title} ${user.position}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  // Control number of displayed entries
  const displayedSalaries = filteredSalaries.slice(0, entriesToShow);

  // Generate and open salary list PDF
  const handleViewPDF = async () => {
    const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  // Start inline editing for a salary record
  const startEditing = (salary) => {
    console.log("Editing Salary:", salary); // Debugging
    setEditingId(salary.id);

    // Ensure `job_id` is properly set by finding the job based on Job_Title
    const job = jobs.find((job) => job.Job_Title === salary.Job_Title);
    setEditedSalary({
      job_id: job ? job.id : "", // Ensure correct job_id is stored
      position: salary.position,
      salary: salary.salary,
    });
  };

  // Cancel editing and reset state
  const cancelEditing = () => {
    setEditingId(null);
    setEditedSalary({});
  };

  // Handle input changes in the edit form
  const handleEditChange = (e, field) => {
    const value = e.target.value;
    console.log(`Field: ${field}, Value: ${value}`); // Debugging
    setEditedSalary((prev) => ({ ...prev, [field]: value }));
  };

  // Save updated salary record
  const saveSalary = async (id) => {
    console.log("Saving Salary:", editedSalary); // Debugging

    try {
      await axios.patch(`http://localhost:5000/salary/${id}`, {
        depId: editedSalary.job_id, // Ensure this is correctly set
        position: editedSalary.position,
        salary: editedSalary.salary,
      });

      getUsers(); // Refresh data after update
      setEditingId(null);
    } catch (error) {
      console.error("Error updating salary:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      {/* Full-Screen Background with Responsive Fix */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed", // Makes it responsive when scrolling
          zIndex: -2, // Push it to the back
        }}
      />
      {/* Dark Overlay for Readability */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)", // Dark overlay
          zIndex: -1,
        }}
      />

      {/* Main Content */}
      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        {/* Search and Action Buttons */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input mt-2"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="mt-2">
            <Link to="/home/AddSalary" className="button is-success">
              Add Position
            </Link>
            <button onClick={handleViewPDF} className="button is-info ml-3">
              View PDF
            </button>
          </div>
        </div>

        {/* Responsive Table & Cards */}
        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Salary ID</th>
                <th>Job Title</th>
                <th>Position / Level</th>
                <th>Salary</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {displayedSalaries.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>
                    {editingId === user.id ? (
                      <div className="select is-fullwidth">
                        <select
                          value={editedSalary.job_id || ""}
                          onChange={(e) => handleEditChange(e, "job_id")}
                          required
                        >
                          <option value="">Select a department</option>
                          {jobs.map((dep) => (
                            <option key={dep.id} value={dep.id}>
                              {dep.Job_Title}
                            </option>
                          ))}
                        </select>
                      </div>
                    ) : (
                      user.Job_Title
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedSalary.position || ""}
                        onChange={(e) => handleEditChange(e, "position")}
                      />
                    ) : (
                      user.position
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="number"
                        className="input"
                        value={editedSalary.salary || ""}
                        onChange={(e) => handleEditChange(e, "salary")}
                      />
                    ) : (
                      `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })}`
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <>
                        <button
                          className="button is-small is-success mr-2"
                          onClick={() => saveSalary(user.id)}
                        >
                          Save
                        </button>
                        <button
                          className="button is-small is-danger"
                          onClick={cancelEditing}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="button is-small is-info"
                        onClick={() => startEditing(user)}
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="columns is-multiline">
            {displayedSalaries.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{user.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Salary ID:</strong> {user.id}
                    </p>
                    <p>
                      <strong>Position:</strong> {user.position}
                    </p>
                    <p>
                      <strong>Salary:</strong>{" "}
                      {user.salary
                        ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                            minimumFractionDigits: 2,
                          })}`
                        : "N/A"}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/editSalary/${user.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer Controls */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredSalaries.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedSalaries.length} of {filteredSalaries.length}{" "}
            entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default SalaryList;
