import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";
// import AddEmployeeModal from "../UI/Modal/AddEmployee";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  // const [isModalActive, setIsModalActive] = useState(false);
  const [departments, setDepartments] = useState([]);
  const [positions, setPositions] = useState({});
  const [editRow, setEditRow] = useState(null);
  const [editedData, setEditedData] = useState({});
  const [loadingPositions, setLoadingPositions] = useState({});
  const [isAdding, setIsAdding] = useState(false);
  const [newEmployee, setNewEmployee] = useState({
    full_name: "",
    gender: "",
    email: "",
    job_id: "",
    salary_id: "",
  });
  const [addErrors, setAddErrors] = useState({
    full_name: false,
    gender: false,
    email: false,
    job_id: false,
    salary_id: false,
  });

  useEffect(() => {
    getUsers();
    fetchDepartments();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const fetchDepartments = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setDepartments(response.data);
    } catch (error) {
      console.error(
        "Error fetching departments:",
        error.response?.data || error.message
      );
    }
  };

  const fetchPositionsForJob = async (jobId) => {
    if (!jobId) {
      console.warn("No jobId provided for fetching positions");
      return;
    }
    console.log(`Starting fetch for jobId: ${jobId}`);
    setLoadingPositions((prev) => ({ ...prev, [jobId]: true }));
    try {
      const response = await axios.get(
        `http://localhost:5000/salary/getPosition/${jobId}`
      );
      console.log(`Positions fetched for job ${jobId}:`, response.data);
      setPositions((prev) => {
        const newPositions = { ...prev, [jobId]: response.data };
        return newPositions;
      });
    } catch (error) {
      console.error(`Error fetching positions for job ${jobId}:`, {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data,
      });
      if (error.response?.status === 404) {
        setPositions((prev) => ({ ...prev, [jobId]: [] }));
      } else {
        setPositions((prev) => ({ ...prev, [jobId]: [] }));
      }
    } finally {
      setLoadingPositions((prev) => {
        const newLoading = { ...prev, [jobId]: false };
        return newLoading;
      });
    }
  };

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
      const uniqueJobIds = [
        ...new Set(response.data.map((user) => user.job_id).filter(Boolean)),
      ];
      await Promise.all(
        uniqueJobIds.map((jobId) => fetchPositionsForJob(jobId))
      );
    } catch (error) {
      console.error(
        "Error fetching users:",
        error.response?.data || error.message
      );
    }
  };

  const handleEdit = async (user) => {
    setEditRow(user.id);
  
    const matchedJob = departments.find(
      (dep) => dep.Job_Title === user.Job_Title
    );
    const jobId = matchedJob ? matchedJob.id : user.job_id || "";
  
    if (jobId && !positions[jobId] && !loadingPositions[jobId]) {
      await fetchPositionsForJob(jobId); // Wait for positions to be fetched
    }
  
    // Wait for the state to update before proceeding
    setTimeout(() => {
      const updatedPositions = positions[jobId] || [];
      if (!Array.isArray(updatedPositions)) {
        console.error(`Positions for job ${jobId} is not an array`, updatedPositions);
        return;
      }
  
      const matchedPosition = updatedPositions.find(
        (pos) => pos.position === user.position
      );
  
      console.log("Editing user position ID:", matchedPosition?.id);
  
      setEditedData({
        full_name: user.full_name || "",
        gender: user.gender || "",
        email: user.email || "",
        job_id: jobId.toString(),
        job_title: user.Job_Title || "",
        salary_id: matchedPosition ? matchedPosition.id.toString() : "",
        salary: matchedPosition ? matchedPosition.salary.toString() : user.salary || "",
      });
    }, 0); // Allow state to update before running
  };
  

  const handleEditChange = async (e, field) => {
    const value = e.target.value;
    setEditedData((prev) => {
      const newData = { ...prev, [field]: value };

      if (field === "job_id") {
        console.log(`Job ID changed to ${value}`);
        fetchPositionsForJob(value).then(() => {
          setEditedData((prevUpdated) => ({
            ...prevUpdated,
            salary_id: "",
            salary: "",
          }));
        });
      }

      if (field === "salary_id") {
        const selectedPosition = positions[prev.job_id]?.find(
          (pos) => pos.id.toString() === value
        );

        setEditedData((prevUpdated) => ({
          ...prevUpdated,
          salary: selectedPosition ? selectedPosition.salary.toString() : "",
        }));
      }

      return newData;
    });
  };

  const handleSave = async (id) => {
    try {
      const updateData = {
        name: editedData.full_name,
        gender: editedData.gender,
        email: editedData.email,
        position_id: parseInt(editedData.salary_id),
        job_id: parseInt(editedData.job_id),
      };
      const response = await axios.patch(
        `http://localhost:5000/users/${id}`,
        updateData
      );
      console.log(`Update response for user ${id}:`, response.data);

      setEditRow(null);
      await getUsers();
    } catch (error) {
      console.error("Error updating user:", {
        message: error.message,
        status: error.response?.status,
        data: error.response?.data,
      });
    }
  };

  const startAdding = () => {
    setIsAdding(true);
    setNewEmployee({
      full_name: "",
      gender: "",
      email: "",
      job_id: "",
      salary_id: "",
    });
    setAddErrors({
      full_name: false,
      gender: false,
      email: false,
      job_id: false,
      salary_id: false,
    });
  };

  const cancelAdding = () => {
    setIsAdding(false);
    setNewEmployee({
      full_name: "",
      gender: "",
      email: "",
      job_id: "",
      salary_id: "",
    });
    setAddErrors({
      full_name: false,
      gender: false,
      email: false,
      job_id: false,
      salary_id: false,
    });
  };

  const handleNewChange = (e, field) => {
    const value = e.target.value;
    setNewEmployee((prev) => {
      const newData = { ...prev, [field]: value };
      if (field === "job_id") {
        fetchPositionsForJob(value);
        newData.salary_id = "";
      }
      console.log("New employee data updated:", newData);
      return newData;
    });
    setAddErrors((prev) => ({
      ...prev,
      [field]: !value || value.trim() === "",
    }));
  };

  const saveNewEmployee = async () => {
    const newAddErrors = {
      full_name: !newEmployee.full_name || newEmployee.full_name.trim() === "",
      gender: !newEmployee.gender || newEmployee.gender.trim() === "",
      email: !newEmployee.email || newEmployee.email.trim() === "",
      job_id: !newEmployee.job_id || newEmployee.job_id.trim() === "",
      salary_id: !newEmployee.salary_id || newEmployee.salary_id.trim() === "",
    };

    if (Object.values(newAddErrors).some((error) => error)) {
      setAddErrors(newAddErrors);
      console.log("Validation failed for new employee:", newAddErrors);
      return;
    }

    try {
      const payload = {
        name: newEmployee.full_name,
        gender: newEmployee.gender,
        email: newEmployee.email,
        position_id: parseInt(newEmployee.salary_id),
        job_id: parseInt(newEmployee.job_id),
      };
      console.log("Saving new employee with Payload:", payload);
      const response = await axios.post("http://localhost:5000/users", payload);
      console.log("POST Response:", response.data);

      setNewEmployee({
        full_name: "",
        gender: "",
        email: "",
        job_id: "",
        salary_id: "",
      });
      setAddErrors({
        full_name: false,
        gender: false,
        email: false,
        job_id: false,
        salary_id: false,
      });
      await getUsers();
      console.log("Employee added successfully!");
    } catch (error) {
      console.error(
        "Error adding employee:",
        error.response?.data || error.message
      );
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title || ""} ${
      user.position || ""
    } ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const deleteUser = async () => {
    if (!deleteData) return;
    try {
      await axios.delete(`http://localhost:5000/users/${deleteData.id}`);
      setDeleteData(null);
      setOpen(false);
      getUsers();
    } catch (error) {
      console.error(
        "Error deleting user:",
        error.response?.data || error.message
      );
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={users} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div style={{ position: "relative" }}>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="is-flex">
            {!isMobile && !isAdding && (
              <button className="button is-success mr-1" onClick={startAdding}>
                Add Employee
              </button>
            )}
            {isMobile ? (
              <Link to="/home/AddEmployee" className="button is-success mr-1">
                Add Employee
              </Link>
            ) : (
              <div>
                {/* <button
                  className="button is-success mr-1"
                  onClick={() => setIsModalActive(true)}
                >
                  Add via Modal
                </button> */}
                {/* <AddEmployeeModal
                  isOpen={isModalActive}
                  onClose={() => setIsModalActive(false)}
                /> */}
              </div>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        {!isMobile ? (
          <div className="table-container overflow-x-auto">
            <table className="table is-striped is-fullwidth">
              <thead>
                <tr>
                  <th>Employee ID</th>
                  <th>Full Name</th>
                  <th>Gender</th>
                  <th>Email</th>
                  <th>Job Title</th>
                  <th>Position</th>
                  <th>Salary</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {isAdding && (
                  <tr>
                    <td>New</td>
                    <td>
                      <input
                        type="text"
                        className="input"
                        value={newEmployee.full_name}
                        onChange={(e) => handleNewChange(e, "full_name")}
                        placeholder="Full Name"
                        style={{
                          borderColor: addErrors.full_name ? "red" : "",
                        }}
                      />
                    </td>
                    <td>
                      <div className="select is-fullwidth">
                        <select
                          value={newEmployee.gender}
                          onChange={(e) => handleNewChange(e, "gender")}
                          style={{ borderColor: addErrors.gender ? "red" : "" }}
                        >
                          <option value="">Select Gender</option>
                          <option value="Male">Male</option>
                          <option value="Female">Female</option>
                        </select>
                      </div>
                    </td>
                    <td>
                      <input
                        type="email"
                        className="input"
                        value={newEmployee.email}
                        onChange={(e) => handleNewChange(e, "email")}
                        placeholder="Email"
                        style={{ borderColor: addErrors.email ? "red" : "" }}
                      />
                    </td>
                    <td>
                      <div className="select is-fullwidth">
                        <select
                          value={newEmployee.job_id}
                          onChange={(e) => handleNewChange(e, "job_id")}
                          style={{ borderColor: addErrors.job_id ? "red" : "" }}
                        >
                          <option value="">Select Job Title</option>
                          {departments.map((dep) => (
                            <option key={dep.id} value={dep.id.toString()}>
                              {dep.Job_Title}
                            </option>
                          ))}
                        </select>
                      </div>
                    </td>
                    <td>
                      <div className="select is-fullwidth">
                        <select
                          value={newEmployee.salary_id}
                          onChange={(e) => handleNewChange(e, "salary_id")}
                          disabled={
                            !newEmployee.job_id ||
                            loadingPositions[newEmployee.job_id] ||
                            !positions[newEmployee.job_id]?.length
                          }
                          style={{
                            borderColor: addErrors.salary_id ? "red" : "",
                          }}
                        >
                          <option value="">Select Position</option>
                          {loadingPositions[newEmployee.job_id] ? (
                            <option disabled>Loading positions...</option>
                          ) : positions[newEmployee.job_id]?.length ? (
                            positions[newEmployee.job_id].map((pos) => (
                              <option key={pos.id} value={pos.id.toString()}>
                                {pos.position}
                              </option>
                            ))
                          ) : (
                            <option disabled>No positions available</option>
                          )}
                        </select>
                      </div>
                    </td>
                    <td>
                      <input
                        type="text"
                        className="input"
                        value={
                          newEmployee.salary_id && positions[newEmployee.job_id]
                            ? `₱${positions[newEmployee.job_id]
                                .find(
                                  (pos) =>
                                    pos.id.toString() === newEmployee.salary_id
                                )
                                ?.salary.toLocaleString("en-PH", {
                                  minimumFractionDigits: 2,
                                })}`
                            : ""
                        }
                        disabled
                      />
                    </td>
                    <td className="is-flex">
                      <button
                        className="button is-small is-success mr-2"
                        onClick={saveNewEmployee}
                      >
                        Save
                      </button>
                      <button
                        className="button is-small is-danger"
                        onClick={cancelAdding}
                      >
                        Cancel
                      </button>
                    </td>
                  </tr>
                )}
                {displayedUsers.length > 0 ? (
                  displayedUsers.map((user) => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            className="input"
                            value={editedData.full_name || ""}
                            onChange={(e) => handleEditChange(e, "full_name")}
                          />
                        ) : (
                          user.full_name
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <div className="select is-fullwidth">
                            <select
                              value={editedData.gender || ""}
                              onChange={(e) => handleEditChange(e, "gender")}
                            >
                              <option value="Male">Male</option>
                              <option value="Female">Female</option>
                            </select>
                          </div>
                        ) : (
                          user.gender
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            className="input"
                            value={editedData.email || ""}
                            onChange={(e) => handleEditChange(e, "email")}
                          />
                        ) : (
                          user.email
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <div className="select is-fullwidth">
                            <select
                              value={editedData.job_id || ""}
                              onChange={(e) => handleEditChange(e, "job_id")}
                            >
                              <option value="">Select Job Title</option>
                              {departments.map((dep) => (
                                <option key={dep.id} value={dep.id.toString()}>
                                  {dep.Job_Title}
                                </option>
                              ))}
                            </select>
                          </div>
                        ) : (
                          user.Job_Title || "-"
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <div className="select is-fullwidth">
                            <select
                              value={editedData.salary_id || ""}
                              onChange={(e) => handleEditChange(e, "salary_id")}
                              disabled={
                                !editedData.job_id ||
                                loadingPositions[editedData.job_id] ||
                                !positions[editedData.job_id]?.length
                              }
                            >
                              <option value="">Select Position</option>
                              {loadingPositions[editedData.job_id] ? (
                                <option disabled>Loading positions...</option>
                              ) : !!positions[editedData.job_id]?.length ? (
                                positions[editedData.job_id].map((pos) => (
                                  <option
                                    key={pos.id}
                                    value={pos.id.toString()}
                                  >
                                    {pos.position}
                                  </option>
                                ))
                              ) : (
                                <option disabled>No positions available</option>
                              )}
                            </select>
                          </div>
                        ) : (
                          user.position || "-"
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <input
                            type="text"
                            className="input"
                            value={
                              editedData.salary
                                ? `₱${Number(editedData.salary).toLocaleString(
                                    "en-PH",
                                    {
                                      minimumFractionDigits: 2,
                                    }
                                  )}`
                                : ""
                            }
                            disabled
                          />
                        ) : (
                          `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                        )}
                      </td>
                      <td>
                        {editRow === user.id ? (
                          <div className="is-flex">
                            <button
                              className="button is-small is-success mr-2"
                              onClick={() => handleSave(user.id)}
                            >
                              Save
                            </button>
                            <button
                              className="button is-small is-danger"
                              onClick={() => setEditRow(null)}
                            >
                              Cancel
                            </button>
                          </div>
                        ) : (
                          <div>
                            <button
                              onClick={() => handleEdit(user)}
                              className="button is-small is-info mr-3"
                            >
                              Edit
                            </button>
                            <button
                              onClick={() => openDelete(user)}
                              className="button is-small is-danger"
                            >
                              Delete
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="8" className="has-text-centered">
                      No users found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="columns is-multiline m-2">
            {displayedUsers.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">
                      {user.full_name} - {user.gender}
                    </p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Employee ID:</strong> {user.id}
                    </p>
                    <p>
                      <strong>Email:</strong> {user.email}
                    </p>
                    <p>
                      <strong>Job Title:</strong> {user.Job_Title || "-"}
                    </p>
                    <p>
                      <strong>Position:</strong> {user.position || "-"}
                    </p>
                    <p>
                      <strong>Salary:</strong> ₱
                      {user.salary?.toLocaleString("en-PH") || "-"}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/edit/${user.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                    <button
                      onClick={() => openDelete(user)}
                      className="card-footer-item button is-small is-danger"
                    >
                      Delete
                    </button>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 p-3 has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredUsers.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedUsers.length} of {filteredUsers.length} entries
          </p>
        </div>

        <ConfirmBox
          open={open}
          closeDialog={() => setOpen(false)}
          title={deleteData?.full_name}
          empID={deleteData?.id}
          deleteFunction={deleteUser}
        />
      </div>
    </div>
  );
};

