import React, { useState } from "react";
import axios from "axios";

function AddDeptModal({ isActive, onClose, fetchJobs }) {
  const [Job_Title, setJobTitle] = useState("");

  const saveJobTitle = async (e) => {
    e.preventDefault();
    if (!Job_Title.trim()) return; // Prevent empty input

    try {
      console.log("Saving Job Title:", Job_Title); // Debugging log
      await axios.post("http://localhost:5000/job", { Job_Title });

      console.log("Job Title added successfully!");
      fetchJobs(); // Refresh job list
      setJobTitle(""); // Clear input field
      onClose(); // Close modal
    } catch (error) {
      console.error("Error adding job title:", error);
    }
  };

  return (
    <div className={`modal ${isActive ? "is-active" : ""}`}>
      <div className="modal-background" onClick={onClose}></div>
      <div className="modal-card">
        <header className="modal-card-head">
          <p className="modal-card-title">Add Job Title</p>
          <button className="delete" aria-label="close" onClick={onClose}></button>
        </header>
        <section className="modal-card-body">
          <form onSubmit={saveJobTitle}>
            <div className="field">
              <label className="label">Job Title</label>
              <div className="control">
                <input
                  type="text"
                  className="input"
                  value={Job_Title}
                  onChange={(e) => setJobTitle(e.target.value)}
                  placeholder="Enter Job Title"
                  required
                />
              </div>
            </div>
          </form>
        </section>
        <footer className="modal-card-foot">
          <button type="submit" className="button is-success" onClick={saveJobTitle}>
            Save
          </button>
          <button className="button" onClick={onClose}>
            Cancel
          </button>
        </footer>
      </div>
    </div>
  );
}

export default AddDeptModal;
