import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const jobTitles = ["Manager", "Developer", "Designer", "Analyst"];
const positions = ["Junior", "Mid", "Senior", "Lead"];

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingUserId, setEditingUserId] = useState(null);
  const [editedData, setEditedData] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const handleEdit = (user) => {
    setEditingUserId(user.id);
    setEditedData({ ...user });
  };

  const handleCancel = () => {
    setEditingUserId(null);
    setEditedData({});
  };

  const handleChange = (e, field) => {
    setEditedData((prev) => ({ ...prev, [field]: e.target.value }));
  };

  const handleSave = async (id) => {
    try {
      await axios.put(`http://localhost:5000/users/${id}`, {
        Job_Title: editedData.Job_Title,
        position: editedData.position,
      });
      getUsers();
      setEditingUserId(null);
      setEditedData({});
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  return (
    <div style={{ position: "relative" }}>
      {/* Background and Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div>
            <Link to="/home/AddEmployee" className="button is-success mr-1">
              Add Employee
            </Link>
            <button className="button is-info ml-1">
              View PDF
            </button>
          </div>
        </div>

        <div className="table-container overflow-x-auto">
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {displayedUsers.length > 0 ? (
                displayedUsers.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.full_name}</td>
                    <td>{user.email}</td>
                    <td>
                      {editingUserId === user.id ? (
                        <div className="select">
                          <select
                            value={editedData.Job_Title}
                            onChange={(e) => handleChange(e, "Job_Title")}
                          >
                            {jobTitles.map((title) => (
                              <option key={title} value={title}>
                                {title}
                              </option>
                            ))}
                          </select>
                        </div>
                      ) : (
                        user.Job_Title
                      )}
                    </td>
                    <td>
                      {editingUserId === user.id ? (
                        <div className="select">
                          <select
                            value={editedData.position}
                            onChange={(e) => handleChange(e, "position")}
                          >
                            {positions.map((pos) => (
                              <option key={pos} value={pos}>
                                {pos}
                              </option>
                            ))}
                          </select>
                        </div>
                      ) : (
                        user.position
                      )}
                    </td>
                    <td>
                      {editingUserId === user.id ? (
                        <>
                          <button className="button is-small is-success mr-1" onClick={() => handleSave(user.id)}>
                            Save
                          </button>
                          <button className="button is-small is-warning" onClick={handleCancel}>
                            Cancel
                          </button>
                        </>
                      ) : (
                        <>
                          <button className="button is-small is-info mr-2" onClick={() => handleEdit(user)}>
                            Edit
                          </button>
                          <button className="button is-small is-danger" onClick={() => setOpen(true)}>
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="has-text-centered">
                    No users found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <ConfirmBox open={open} closeDialog={() => setOpen(false)} title={deleteData?.full_name} empID={deleteData?.id} deleteFunction={() => {}} />
      </div>
    </div>
  );
};

export default UserList;

