import React, { useState } from "react";
import { motion } from "framer-motion";

const Resume = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null); // State for full-screen image

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.8,
        ease: "easeOut",
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 },
  };

  // Open full-screen image
  const handleImageClick = (imgSrc) => {
    setSelectedImage(imgSrc);
  };

  // Close full-screen image
  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  return (
    <>
      <div
        className="is-flex is-justify-content-center is-align-items-center"
        style={{
          minHeight: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          padding: "4rem 2rem",
          overflow: "hidden",
          position: "relative",
        }}
      >
        {/* Overlay */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: darkMode ? "rgba(0, 0, 0, 0.5)" : "rgba(255, 255, 255, 0.3)",
            zIndex: 1,
          }}
        />

        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className={`box ${darkMode ? "dark-theme" : "light-theme"}`}
          style={{
            maxWidth: "1100px",
            width: "100%",
            padding: "3rem",
            borderRadius: "24px",
            boxShadow: darkMode
              ? "0 15px 60px rgba(255,255,255,0.1)"
              : "0 15px 60px rgba(0,0,0,0.2)",
            background: darkMode ? "rgba(30, 30, 30, 0.95)" : "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(15px)",
            border: darkMode ? "1px solid rgba(255,255,255,0.1)" : "1px solid rgba(0,0,0,0.05)",
            position: "relative",
            zIndex: 2,
          }}
        >
          {/* Dark Mode Toggle */}
          <motion.div className="has-text-right mb-5" whileHover={{ scale: 1.05 }}>
            <motion.button
              whileHover={{ scale: 1.1, rotate: 5 }}
              whileTap={{ scale: 0.95 }}
              className={`button is-rounded ${darkMode ? "is-white" : "is-dark"}`}
              onClick={() => setDarkMode(!darkMode)}
              style={{
                border: "none",
                padding: "0.75rem 1.5rem",
                background: darkMode
                  ? "linear-gradient(45deg, #ff6b6b, #ff8e53)"
                  : "linear-gradient(45deg, #4facfe, #00f2fe)",
                color: darkMode ? "#1a1a1a" : "#fff",
              }}
            >
              {darkMode ? "Light Mode" : "Dark Mode"}
            </motion.button>
          </motion.div>

          {/* Profile Header */}
          <motion.div variants={itemVariants} className="has-text-centered mb-6">
            <motion.img
              whileHover={{ scale: 1.05, rotate: 2 }}
              src="/profile.jpg"
              alt="Ryan Santiago"
              style={{
                borderRadius: "50%",
                width: "160px",
                height: "160px",
                objectFit: "cover",
                border: `4px solid ${darkMode ? "#ff6b6b" : "#4facfe"}`,
                boxShadow: "0 8px 25px rgba(0,0,0,0.3)",
              }}
            />
            <motion.h1
              className="title is-3 mt-4"
              variants={itemVariants}
              style={{
                color: darkMode ? "#fff" : "#333",
                background: darkMode
                  ? "linear-gradient(45deg, #ff6b6b, #ff8e53)"
                  : "linear-gradient(45deg, #4facfe, #00f2fe)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
              }}
            >
              Ryan C. Santiago
            </motion.h1>
            <motion.p
              className="subtitle is-5"
              variants={itemVariants}
              style={{ color: darkMode ? "#ccc" : "#666" }}
            >
              Full-Stack Developer & IT Specialist
            </motion.p>
          </motion.div>

          <hr
            style={{
              background: darkMode ? "rgba(255,255,255,0.2)" : "rgba(0,0,0,0.1)",
              height: "2px",
              border: "none",
              margin: "2rem 0",
            }}
          />

          {/* Profile Section */}
          <motion.section variants={itemVariants}>
            <h3 className="title is-4" style={{ color: darkMode ? "#ff6b6b" : "#4facfe" }}>
              Profile
            </h3>
            <p style={{ color: darkMode ? "#ddd" : "#555" }}>
              GitHub:{" "}
              <motion.a
                href="https://github.com/Ryalynne"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.05, color: darkMode ? "#ff8e53" : "#00f2fe" }}
              >
                github.com/Ryalynne
              </motion.a>
            </p>
          </motion.section>

          {/* Projects Section */}
          <motion.section variants={itemVariants} className="mt-6">
            <h3 className="title is-4" style={{ color: darkMode ? "#ff6b6b" : "#4facfe" }}>
              Featured Projects
            </h3>
            <div className="columns is-multiline">
              {[
                { name: "Library System with Inventory & QR Code", img: "/BMALibrary.png" },
                { name: "BMA Portal", img: null },
                { name: "MilkTea Inventory & POS", img: "/milktea.png" },
                { name: "BMA Attendance QR-Code", img: null },
                { name: "Mini-Grocery", img: "/POSANDINVENTORY.jpg" },
                { name: "Clearance System", img: "/cleranceSystem.png" },
                { name: "BMA Landing Page", img: "/BMALanding.png" },
              ].map((project, index) => (
                <motion.div
                  key={index}
                  className="column is-6"
                  whileHover={{ y: -10 }}
                  variants={itemVariants}
                >
                  <div
                    style={{
                      background: darkMode ? "rgba(255,255,255,0.05)" : "rgba(0,0,0,0.05)",
                      padding: "1.5rem",
                      borderRadius: "12px",
                      transition: "all 0.3s",
                    }}
                  >
                    <motion.p
                      whileHover={{ scale: 1.02 }}
                      style={{ color: darkMode ? "#fff" : "#333" }}
                    >
                      {project.name}
                    </motion.p>
                    {project.img ? (
                      <motion.img
                        src={project.img}
                        alt={project.name}
                        whileHover={{ scale: 1.05 }}
                        onClick={() => handleImageClick(project.img)} // Click to show full screen
                        style={{
                          width: "100%",
                          borderRadius: "8px",
                          marginTop: "1rem",
                          boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
                          cursor: "pointer", // Indicate clickable
                        }}
                      />
                    ) : (
                      <p style={{ color: darkMode ? "#888" : "#999" }}>No Image Available</p>
                    )}
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.section>

          {/* Contact Section */}
          <motion.section variants={itemVariants} className="mt-6">
            <h3 className="title is-4" style={{ color: darkMode ? "#ff6b6b" : "#4facfe" }}>
              Contact
            </h3>
            <div style={{ color: darkMode ? "#ddd" : "#555" }}>
              <p><strong>Location:</strong> Taal, Pulilan, Bulacan</p>
              <p><strong>Phone:</strong> 0926-695-5822</p>
              <p><strong>Email:</strong> santiagoryan788@gmail.com</p>
            </div>
          </motion.section>
        </motion.div>
      </div>

      {/* Full-Screen Image Modal */}
      {selectedImage && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={handleCloseModal} // Close on overlay click
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            background: "rgba(0, 0, 0, 0.9)", // Dark overlay
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000, // Ensure it’s on top
          }}
        >
          <motion.img
            src={selectedImage}
            alt="Full Screen"
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.3 }}
            style={{
              maxWidth: "90%",
              maxHeight: "90%",
              objectFit: "contain",
              borderRadius: "8px",
              boxShadow: "0 10px 30px rgba(0,0,0,0.5)",
            }}
            onClick={(e) => e.stopPropagation()} // Prevent closing when clicking image
          />
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={handleCloseModal}
            style={{
              position: "absolute",
              top: "20px",
              right: "20px",
              background: "#ff6b6b",
              border: "none",
              borderRadius: "50%",
              width: "40px",
              height: "40px",
              color: "#fff",
              fontSize: "20px",
              cursor: "pointer",
            }}
          >
            ×
          </motion.button>
        </motion.div>
      )}
    </>
  );
};

export default Resume;
