import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  // State Hooks
  const [salaries, setSalary] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  const [jobs, setJobs] = useState([]);
  const [newSalary, setNewSalary] = useState({
    dep_id: "",
    Position: "",
    Salary: "",
  });
  const [isAdding, setIsAdding] = useState(false);

  // Effect Hook for fetching data and handling window resize
  useEffect(() => {
    getUsers();
    getJobs();

    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Fetch salary data from API
  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      console.log("Fetched salaries:", response.data);
      const sanitizedSalaries = (response.data || []).map((salary) => ({
        ...salary,
        Job_Title: salary.Job_Title || "Untitled",
        position: salary.Position || salary.position || "N/A",
        salary: salary.Salary || salary.salary || "0",
      }));
      setSalary(sanitizedSalaries);
    } catch (error) {
      console.error("Error fetching salaries:", error);
      setSalary([]);
    }
  };

  // Fetch job titles from API
  const getJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      console.log("Fetched jobs:", response.data);
      setJobs(response.data || []);
    } catch (error) {
      console.error("Error fetching job titles:", error);
      setJobs([]);
    }
  };

  // Filter salaries based on search input
  const filteredSalaries = salaries.filter((user) =>
    `${user.Job_Title || ""} ${user.position || ""}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );
  const displayedSalaries = filteredSalaries.slice(0, entriesToShow);

  // Generate and open salary list PDF
  const handleViewPDF = async () => {
    const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  // Start inline editing for a salary record
  const startEditing = (salary) => {
    console.log("Editing Salary:", salary);
    setEditingId(salary.id);
    const job = jobs.find((job) => job.Job_Title === salary.Job_Title);
    setEditedSalary({
      dep_id: job ? job.id : "",
      Position: salary.position || "",
      Salary: salary.salary || "",
    });
  };

  // Cancel editing and reset state
  const cancelEditing = () => {
    setEditingId(null);
    setEditedSalary({});
  };

  // Handle input changes in the edit form
  const handleEditChange = (e, field) => {
    const value = e.target.value;
    console.log(`Edit Field: ${field}, Value: ${value}`);
    setEditedSalary((prev) => ({ ...prev, [field]: value }));
  };

  // Save updated salary record
  const saveSalary = async (id) => {
    console.log("Saving Salary:", editedSalary);
    try {
      await axios.patch(`http://localhost:5000/salary/${id}`, {
        dep_id: editedSalary.dep_id,
        Position: editedSalary.Position,
        Salary: editedSalary.Salary,
      });
      getUsers(); // Refresh data
      setEditingId(null);
      alert("Salary updated successfully!");
    } catch (error) {
      console.error("Error updating salary:", error);
      alert(
        "Failed to update salary: " +
          (error.response?.data?.message || error.message)
      );
    }
  };

  // Start adding a new salary row
  const startAdding = () => {
    setIsAdding(true);
    setNewSalary({ dep_id: "", Position: "", Salary: "" });
  };

  // Cancel adding a new salary and hide the row
  const cancelAdding = () => {
    setIsAdding(false);
    setNewSalary({ dep_id: "", Position: "", Salary: "" });
  };

  // Handle input changes for the new salary row
  const handleNewSalaryChange = (e, field) => {
    const value = e.target.value;
    console.log(`New Salary Field: ${field}, Value: ${value}`);
    setNewSalary((prev) => ({ ...prev, [field]: value }));
  };

  // Save new salary record and keep adding mode active
  const saveNewSalary = async () => {
    if (!newSalary.dep_id || !newSalary.Position || !newSalary.Salary) {
      alert("All fields are required");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/salary", {
        dep_id: newSalary.dep_id,
        Position: newSalary.Position,
        Salary: newSalary.Salary,
      });
      console.log("New salary added:", response.data);
      getUsers(); // Refresh data
      // Reset fields but keep adding mode active
      setNewSalary({ dep_id: "", Position: "", Salary: "" });
      alert("Salary added successfully!");
    } catch (error) {
      console.error("Error adding salary:", error);
      alert(
        "Failed to add salary: " +
          (error.response?.data?.message || error.message)
      );
    }
  };

  return (
    <div style={{ position: "relative" }}>
      {/* Full-Screen Background */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      {/* Dark Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      {/* Main Content */}
      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        {/* Search and Action Buttons */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input mt-2"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="mt-2 is-flex">
            {!isMobile && !isAdding && (
              <button className="button is-success ml-3" onClick={startAdding}>
                Add Position
              </button>
            )}
            {isMobile && (
              <Link to="/home/AddSalary" className="button is-success ml-3">
                Add Position
              </Link>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-3">
              View PDF
            </button>
          </div>
        </div>

        {/* Table View (Desktop) */}
        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Salary ID</th>
                <th>Job Title</th>
                <th>Position / Level</th>
                <th>Salary</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {isAdding && (
                <tr>
                  <td>New</td>
                  <td>
                    <div className="select is-fullwidth">
                      <select
                        value={newSalary.dep_id}
                        onChange={(e) => handleNewSalaryChange(e, "dep_id")}
                        required
                      >
                        <option value="">Select a job</option>
                        {jobs.map((job) => (
                          <option key={job.id} value={job.id}>
                            {job.Job_Title}
                          </option>
                        ))}
                      </select>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newSalary.Position}
                      onChange={(e) => handleNewSalaryChange(e, "Position")}
                      placeholder="Enter position"
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="input"
                      value={newSalary.Salary}
                      onChange={(e) => handleNewSalaryChange(e, "Salary")}
                      placeholder="Enter salary"
                    />
                  </td>
                  <td>
                    <button
                      className="button is-small is-success mr-2"
                      onClick={saveNewSalary}
                    >
                      Save
                    </button>
                    <button
                      className="button is-small is-danger"
                      onClick={cancelAdding}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              )}
              {displayedSalaries.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>
                    {editingId === user.id ? (
                      <div className="select is-fullwidth">
                        <select
                          value={editedSalary.dep_id || ""}
                          onChange={(e) => handleEditChange(e, "dep_id")}
                          required
                        >
                          <option value="">Select a job</option>
                          {jobs.map((dep) => (
                            <option key={dep.id} value={dep.id}>
                              {dep.Job_Title}
                            </option>
                          ))}
                        </select>
                      </div>
                    ) : (
                      user.Job_Title || "Untitled"
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedSalary.Position || ""}
                        onChange={(e) => handleEditChange(e, "Position")}
                      />
                    ) : (
                      user.position || "N/A"
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="number"
                        className="input"
                        value={editedSalary.Salary || ""}
                        onChange={(e) => handleEditChange(e, "Salary")}
                      />
                    ) : user.salary ? (
                      `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2,
                      })}`
                    ) : (
                      "N/A"
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <>
                        <button
                          className="button is-small is-success mr-2"
                          onClick={() => saveSalary(user.id)}
                        >
                          Save
                        </button>
                        <button
                          className="button is-small is-danger"
                          onClick={cancelEditing}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="button is-small is-info"
                        onClick={() => startEditing(user)}
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="columns is-multiline">
            {displayedSalaries.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">
                      {user.Job_Title || "Untitled"}
                    </p>
                  </header>
                  <div className="card-content">
                    <p>
                      <strong>Salary ID:</strong> {user.id}
                    </p>
                    <p>
                      <strong>Position:</strong> {user.position || "N/A"}
                    </p>
                    <p>
                      <strong>Salary:</strong>{" "}
                      {user.salary
                        ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                            minimumFractionDigits: 2,
                          })}`
                        : "N/A"}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/editSalary/${user.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer Controls */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredSalaries.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedSalaries.length} of {filteredSalaries.length}{" "}
            entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default SalaryList;
