import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function DepartList() {
  /*** State Hooks ***/
  const [jobs, setJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingJobId, setEditingJobId] = useState(null);
  const [editedJobTitle, setEditedJobTitle] = useState("");

  /*** Effect Hooks ***/
  useEffect(() => {
    fetchJobs();
    
    // Handle screen size changes
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  /*** API Calls ***/
  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching job titles:", error);
    }
  };

  /*** Filtering Logic ***/
  const filteredJobs = jobs.filter((job) =>
    job.Job_Title.toLowerCase().includes(searchTerm.toLowerCase())
  );
  const displayedJobs = filteredJobs.slice(0, entriesToShow);

  /*** Inline Editing Functions ***/
  const startEditing = (job) => {
    setEditingJobId(job.id);
    setEditedJobTitle(job.Job_Title);
  };

  const cancelEditing = () => {
    setEditingJobId(null);
    setEditedJobTitle("");
  };

  const saveJobTitle = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/job/${id}`, {
        job_title: editedJobTitle,
      });
      
      setJobs(
        jobs.map((job) =>
          job.id === id ? { ...job, Job_Title: editedJobTitle } : job
        )
      );
      setEditingJobId(null);
    } catch (error) {
      console.error("Error updating job title:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      {/* Background and Overlay */}
      <div style={{
          position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')", backgroundSize: "cover",
          backgroundPosition: "center", backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed", zIndex: -2,
        }}
      />
      <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", background: "rgba(0, 0, 0, 0.5)", zIndex: -1 }} />

      {/* Main Content */}
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        {/* Search & Actions */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text" className="input" style={{ maxWidth: "300px" }}
            placeholder="Search Job Title..." value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Link to="/home/AddJob" className="button is-success ml-3">Add Job Title</Link>
        </div>

        {/* Table View (Desktop) */}
        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Job ID</th>
                <th>Job Title</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {displayedJobs.map((job) => (
                <tr key={job.id}>
                  <td>{job.id}</td>
                  <td>
                    {editingJobId === job.id ? (
                      <input type="text" className="input" value={editedJobTitle} onChange={(e) => setEditedJobTitle(e.target.value)} />
                    ) : (
                      job.Job_Title
                    )}
                  </td>
                  <td>
                    {editingJobId === job.id ? (
                      <>
                        <button className="button is-small is-success mr-2" onClick={() => saveJobTitle(job.id)}>Save</button>
                        <button className="button is-small is-danger" onClick={cancelEditing}>Cancel</button>
                      </>
                    ) : (
                      <button className="button is-small is-info mr-2" onClick={() => startEditing(job)}>Edit</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="columns is-multiline">
            {displayedJobs.map((job) => (
              <div key={job.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{job.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p className="subtitle is-6"><strong>Job ID:</strong> {job.id}</p>
                  </div>
                  <footer className="card-footer">
                    <Link to={`/home/editJobTitle/${job.id}`} className="card-footer-item button is-small is-info">Edit</Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer - Entry Controls */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select value={entriesToShow} onChange={(e) => setEntriesToShow(Number(e.target.value))}>
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredJobs.length}>All</option>
              </select>
            </div>
          </div>
          <p>Showing {displayedJobs.length} of {filteredJobs.length} entries</p>
        </div>
      </div>
    </div>
  );
}

export default DepartList;
