import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";

function SalaryList() {
  const [salaries, setSalaries] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  const [newSalary, setNewSalary] = useState({ job_id: "", position: "", salary: "" });
  const [isAdding, setIsAdding] = useState(false);

  useEffect(() => {
    fetchSalaries();
    fetchJobs();

    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const fetchSalaries = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      setSalaries(response.data || []);
    } catch (error) {
      console.error("Error fetching salaries:", error);
    }
  };

  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data || []);
    } catch (error) {
      console.error("Error fetching jobs:", error);
    }
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  const startAdding = () => {
    setIsAdding(true);
    setNewSalary({ job_id: "", position: "", salary: "" });
  };

  const cancelAdding = () => {
    setIsAdding(false);
    setNewSalary({ job_id: "", position: "", salary: "" });
  };

  const handleNewSalaryChange = (e, field) => {
    setNewSalary((prev) => ({ ...prev, [field]: e.target.value }));
  };

  const saveNewSalary = async () => {
    console.log("Saving new salary:", newSalary);

    if (!newSalary.job_id || !newSalary.position.trim() || !newSalary.salary.trim()) {
      alert("Please fill in all fields correctly.");
      return;
    }

    const salaryToSave = {
      job_id: newSalary.job_id,
      position: newSalary.position,
      salary: parseFloat(newSalary.salary),
    };

    try {
      const response = await axios.post("http://localhost:5000/salary", salaryToSave);
      console.log("Salary added:", response.data);
      fetchSalaries();
      cancelAdding();
      alert("Salary added successfully!");
    } catch (error) {
      console.error("Error adding salary:", error.response || error);
      alert("Failed to add salary. Check console for details.");
    }
  };

  return (
    <div>
      <div className="container mt-5">
        <div className="is-flex is-justify-content-space-between mb-3">
          <input
            type="text"
            className="input"
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div>
            {!isMobile && !isAdding && (
              <button className="button is-success" onClick={startAdding}>Add Position</button>
            )}
            {isMobile && (
              <Link to="/home/AddSalary" className="button is-success">Add Position</Link>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-3">View PDF</button>
          </div>
        </div>

        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>ID</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {isAdding && (
                <tr>
                  <td>New</td>
                  <td>
                    <div className="select">
                      <select value={newSalary.job_id} onChange={(e) => handleNewSalaryChange(e, "job_id")}>
                        <option value="">Select Job</option>
                        {jobs.map((job) => (
                          <option key={job.id} value={job.id}>{job.Job_Title}</option>
                        ))}
                      </select>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newSalary.position}
                      onChange={(e) => handleNewSalaryChange(e, "position")}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="input"
                      value={newSalary.salary}
                      onChange={(e) => handleNewSalaryChange(e, "salary")}
                      min="0"
                    />
                  </td>
                  <td>
                    <button className="button is-success" onClick={saveNewSalary}>Save</button>
                    <button className="button is-danger" onClick={cancelAdding}>Cancel</button>
                  </td>
                </tr>
              )}
              {salaries.map((salary) => (
                <tr key={salary.id}>
                  <td>{salary.id}</td>
                  <td>{salary.Job_Title}</td>
                  <td>{salary.position}</td>
                  <td>{`₱${parseFloat(salary.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}`}</td>
                  <td>
                    <button className="button is-info">Edit</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="columns is-multiline">
            {salaries.map((salary) => (
              <div key={salary.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{salary.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p><strong>ID:</strong> {salary.id}</p>
                    <p><strong>Position:</strong> {salary.position}</p>
                    <p><strong>Salary:</strong> ₱{parseFloat(salary.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}</p>
                  </div>
                  <footer className="card-footer">
                    <Link to={`/home/editSalary/${salary.id}`} className="card-footer-item button is-small is-info">
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default SalaryList;
