import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editRowId, setEditRowId] = useState(null);
  const [editedUser, setEditedUser] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const handleEdit = (user) => {
    setEditRowId(user.id);
    setEditedUser({ ...user });
  };

  const handleInputChange = (e, field) => {
    setEditedUser({ ...editedUser, [field]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/users/${editRowId}`, editedUser);
      setEditRowId(null);
      getUsers();
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div className="table-container overflow-x-auto">
      <table className="table is-striped is-fullwidth">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Job Title</th>
            <th>Position</th>
            <th>Salary</th>
            <th>Annual Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>
                {editRowId === user.id ? (
                  <input
                    type="text"
                    className="input"
                    value={editedUser.full_name}
                    onChange={(e) => handleInputChange(e, "full_name")}
                  />
                ) : (
                  user.full_name
                )}
              </td>
              <td>{user.email}</td>
              <td>
                {editRowId === user.id ? (
                  <input
                    type="text"
                    className="input"
                    value={editedUser.Job_Title}
                    onChange={(e) => handleInputChange(e, "Job_Title")}
                  />
                ) : (
                  user.Job_Title
                )}
              </td>
              <td>
                {editRowId === user.id ? (
                  <input
                    type="text"
                    className="input"
                    value={editedUser.position}
                    onChange={(e) => handleInputChange(e, "position")}
                  />
                ) : (
                  user.position
                )}
              </td>
              <td>â‚±{user.salary?.toLocaleString("en-PH") || "-"}</td>
              <td>â‚±{user.anual_salary?.toLocaleString("en-PH") || "-"}</td>
              <td>
                {editRowId === user.id ? (
                  <button onClick={handleSave} className="button is-small is-success">
                    Save
                  </button>
                ) : (
                  <button
                    onClick={() => handleEdit(user)}
                    className="button is-small is-warning mr-2"
                  >
                    Edit
                  </button>
                )}
                <button
                  onClick={() => openDelete(user)}
                  className="button is-small is-danger"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
