import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = ({ displayedUsers, jobTitles, positions, onSave }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingRow, setEditingRow] = useState(null);
  const [editedData, setEditedData] = useState({});

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleEdit = (user) => {
    setEditingRow(user.id);
    setEditedData({
      jobTitle: user.Job_Title || "",
      position: user.position || "",
      salary: user.salary || "",
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = (id) => {
    onSave(id, editedData);
    setEditingRow(null);
  };

  const openDelete = (user) => {
    setDeleteData(user);
    setOpen(true);
  };

  const handleViewPDF = async () => {
    const blob = await pdf(<EmployePDF users={displayedUsers} />).toBlob();
    window.open(URL.createObjectURL(blob));
  };

  return (
    <div className="container mt-5">
      <div className="is-flex is-justify-content-space-between m-2">
        <input
          type="text"
          className="input"
          style={{ maxWidth: "300px" }}
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div>
          <Link to="/home/AddEmployee" className="button is-success mr-1">
            Add Employee
          </Link>
          <button onClick={handleViewPDF} className="button is-info ml-1">
            View PDF
          </button>
        </div>
      </div>

      {!isMobile ? (
        <div className="table-container overflow-x-auto">
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {displayedUsers.length > 0 ? (
                displayedUsers.map((user) => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.full_name}</td>
                    <td>{user.email}</td>
                    <td>
                      {editingRow === user.id ? (
                        <select
                          name="jobTitle"
                          value={editedData.jobTitle}
                          onChange={handleChange}
                          className="input"
                        >
                          {jobTitles.map((title) => (
                            <option key={title} value={title}>{title}</option>
                          ))}
                        </select>
                      ) : (
                        user.Job_Title
                      )}
                    </td>
                    <td>
                      {editingRow === user.id ? (
                        <select
                          name="position"
                          value={editedData.position}
                          onChange={handleChange}
                          className="input"
                        >
                          {positions.map((pos) => (
                            <option key={pos} value={pos}>{pos}</option>
                          ))}
                        </select>
                      ) : (
                        user.position
                      )}
                    </td>
                    <td>
                      {editingRow === user.id ? (
                        <input
                          type="number"
                          name="salary"
                          value={editedData.salary}
                          onChange={handleChange}
                          className="input"
                        />
                      ) : (
                        `₱${user.salary?.toLocaleString("en-PH") || "-"}`
                      )}
                    </td>
                    <td>
                      {editingRow === user.id ? (
                        <button
                          onClick={() => handleSave(user.id)}
                          className="button is-small is-success mr-2"
                        >
                          Save
                        </button>
                      ) : (
                        <button
                          onClick={() => handleEdit(user)}
                          className="button is-small is-warning mr-2"
                        >
                          Edit
                        </button>
                      )}
                      <button
                        onClick={() => openDelete(user)}
                        className="button is-small is-danger"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="7" className="has-text-centered">No users found.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      ) : null}

      <ConfirmBox
        open={open}
        closeDialog={() => setOpen(false)}
        title={deleteData?.full_name}
        empID={deleteData?.id}
        deleteFunction={() => setOpen(false)}
      />
    </div>
  );
};

export default UserList;
