import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import EmployePDF from "../Printing/EmployeePDF";
import ConfirmBox from "../UI/Modal/ConfirmBox";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [deleteData, setDeleteData] = useState(null);
  const [open, setOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingUserId, setEditingUserId] = useState(null);
  const [editedUser, setEditedUser] = useState({});

  useEffect(() => {
    getUsers();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users");
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const filteredUsers = users.filter((user) =>
    `${user.full_name} ${user.email} ${user.Job_Title} ${user.position} ${user.id}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedUsers = filteredUsers.slice(0, entriesToShow);

  const startEditing = (user) => {
    setEditingUserId(user.id);
    setEditedUser(user);
  };

  const cancelEditing = () => {
    setEditingUserId(null);
    setEditedUser({});
  };

  const saveUser = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/users/${id}`, editedUser);
      setUsers(
        users.map((user) => (user.id === id ? { ...user, ...editedUser } : user))
      );
      setEditingUserId(null);
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between m-2">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Full Name, Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Full Name</th>
                <th>Email</th>
                <th>Job Title</th>
                <th>Position</th>
                <th>Salary</th>
                <th>Annual Salary</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {displayedUsers.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>
                    {editingUserId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedUser.full_name || ""}
                        onChange={(e) => setEditedUser({ ...editedUser, full_name: e.target.value })}
                      />
                    ) : (
                      user.full_name
                    )}
                  </td>
                  <td>{user.email}</td>
                  <td>
                    {editingUserId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedUser.Job_Title || ""}
                        onChange={(e) => setEditedUser({ ...editedUser, Job_Title: e.target.value })}
                      />
                    ) : (
                      user.Job_Title
                    )}
                  </td>
                  <td>
                    {editingUserId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedUser.position || ""}
                        onChange={(e) => setEditedUser({ ...editedUser, position: e.target.value })}
                      />
                    ) : (
                      user.position
                    )}
                  </td>
                  <td>₱{user.salary?.toLocaleString("en-PH") || "-"}</td>
                  <td>₱{user.anual_salary?.toLocaleString("en-PH") || "-"}</td>
                  <td>
                    {editingUserId === user.id ? (
                      <>
                        <button className="button is-small is-success mr-2" onClick={() => saveUser(user.id)}>
                          Save
                        </button>
                        <button className="button is-small is-danger" onClick={cancelEditing}>
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button className="button is-small is-info mr-2" onClick={() => startEditing(user)}>
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : null}
      </div>
    </div>
  );
};

export default UserList;

