import { StyleSheet, Font } from "@react-pdf/renderer";

// Register the Poppins font
Font.register({
  family: "Poppins",
  src: "https://raw.githubusercontent.com/google/fonts/main/ofl/poppins/Poppins-Regular.ttf",
});

const styles = StyleSheet.create({
  page: {
    padding: 30,
    fontSize: 10,
    fontFamily: "Poppins",
    backgroundColor: "#ffffff",
  },

  header: {
    fontSize: 18,
    textAlign: "center",
    marginBottom: 25,
    fontWeight: "bold",
    textTransform: "uppercase",
    color: "#34495E",
    letterSpacing: 1,
  },

  table: {
    display: "table",
    width: "100%",
    border: "1px solid #ddd", // Outer table border
    borderRadius: 5,
  },

  tableHeader: {
    flexDirection: "row",
    backgroundColor: "#34495E",
    padding: 8,
    borderBottom: "1px solid #2C3E50", // Header bottom border
  },

  tableHeaderCell: {
    flex: 1,
    color: "#FFFFFF",
    fontWeight: "bold",
    textAlign: "center",
    fontSize: 10,
    textTransform: "uppercase",
    letterSpacing: 0.5,
    borderRight: "1px solid #FFFFFF", // Vertical borders between header cells
  },

  emailHeaderCell: {
    flex: 2.5,
    color: "#FFFFFF",
    fontWeight: "bold",
    textAlign: "center",
    fontSize: 10,
    textTransform: "uppercase",
    letterSpacing: 0.5,
    borderRight: "1px solid #FFFFFF",
  },

  tableRow: {
    flexDirection: "row",
    borderBottom: "1px solid #e0e0e0", // Row bottom border
    backgroundColor: "#ffffff",
  },

  tableRowAlternate: {
    backgroundColor: "#f5f5f5",
  },

  tableCell: {
    flex: 1,
    padding: 8,
    fontSize: 10,
    textAlign: "center",
    color: "#2C3E50",
    borderRight: "1px solid #e0e0e0", // Vertical borders between cells
  },

  emailCell: {
    flex: 2.5,
    color: "#2980B9",
    padding: 8,
    fontSize: 10,
    textAlign: "left",
    wordBreak: "break-word",
    borderRight: "1px solid #e0e0e0",
  },

  salaryCell: {
    flex: 1,
    padding: 8,
    fontSize: 10,
    textAlign: "right",
    fontWeight: "bold",
    color: "#27AE60",
    borderRight: "1px solid #e0e0e0",
  },

  datePrinted: { // Added style for date
    fontSize: 10,
    textAlign: "right",
    marginTop: 20,
    color: "#2C3E50",
  },
});

export default styles;
