import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";
import AddSalaryModal from "../UI/Modal/AddSalaryModel";

function SalaryList() {
  // State Hooks (unchanged)
  const [salaries, setSalaries] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});
  const [jobs, setJobs] = useState([]);
  const [isModalActive, setIsModalActive] = useState(false);
  const [isAdding, setIsAdding] = useState(false);
  const [newSalary, setNewSalary] = useState({ dep_id: "", Position: "", Salary: "" });
  const [addErrors, setAddErrors] = useState({ dep_id: false, Position: false, Salary: false });
  const [editErrors, setEditErrors] = useState({ dep_id: false, Position: false, Salary: false });

  // Fetch Data on Mount (unchanged)
  useEffect(() => {
    getUsers();
    getJobs();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      setSalaries(response.data || []);
    } catch (error) {
      console.error("Error fetching salaries:", error);
      setSalaries([]);
    }
  };

  const getJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data || []);
    } catch (error) {
      console.error("Error fetching jobs:", error);
      setJobs([]);
    }
  };

  // Filtering and Pagination (unchanged)
  const filteredSalaries = salaries.filter((user) =>
    `${user.Job_Title} ${user.position}`.toLowerCase().includes(searchTerm.toLowerCase())
  );
  const displayedSalaries = filteredSalaries.slice(0, entriesToShow);

  // PDF Generation (unchanged)
  const handleViewPDF = async () => {
    const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  // Start Editing (unchanged, as it correctly retrieves data)
  const startEditing = (salary) => {
    const job = jobs.find((job) => job.Job_Title === salary.Job_Title);
    const newEditedSalary = {
      dep_id: job ? job.id : "",
      Position: salary.position || "",
      Salary: salary.salary || ""
    };
    setEditingId(salary.id);
    setEditedSalary(newEditedSalary);
    setEditErrors({ dep_id: false, Position: false, Salary: false });
  };

  // Cancel Editing (unchanged)
  const cancelEditing = () => {
    setEditingId(null);
    setEditedSalary({});
    setEditErrors({ dep_id: false, Position: false, Salary: false });
  };

  // Handle Input Changes During Editing (unchanged)
  const handleEditChange = (e, field) => {
    const value = e.target.value;
    setEditedSalary((prev) => ({ ...prev, [field]: value }));
    setEditErrors((prev) => ({ ...prev, [field]: value.trim() === "" }));
  };

  // Revised saveSalary Function
  const saveSalary = async (id) => {
    // Validate Fields
    const newEditErrors = {
      dep_id: !editedSalary.dep_id || editedSalary.dep_id.trim() === "",
      Position: !editedSalary.Position || editedSalary.Position.trim() === "",
      Salary: !editedSalary.Salary || editedSalary.Salary.trim() === ""
    };

    if (newEditErrors.dep_id || newEditErrors.Position || newEditErrors.Salary) {
      console.log("Validation failed:", newEditErrors);
      setEditErrors(newEditErrors);
      return;
    }

    // Prepare payload matching the working "add" functionality
    const payload = {
      dep_id: editedSalary.dep_id, // Kept as string to match saveNewSalary
      Position: editedSalary.Position,
      Salary: editedSalary.Salary
    };

    try {
      console.log("Saving salary with ID:", id, "Payload:", payload);
      await axios.patch(`http://localhost:5000/salary/${id}`, payload);
      console.log("Salary updated successfully");
      await getUsers(); // Refresh data
      setEditingId(null);
      setEditedSalary({});
      setEditErrors({ dep_id: false, Position: false, Salary: false });
    } catch (error) {
      console.error("Error updating salary:", error.response?.data || error.message);
      alert("Failed to update salary: " + (error.response?.data?.message || error.message));
    }
  };

  // Add Functionality (unchanged, as it works)
  const startAdding = () => setIsAdding(true);

  const cancelAdding = () => {
    setIsAdding(false);
    setNewSalary({ dep_id: "", Position: "", Salary: "" });
    setAddErrors({ dep_id: false, Position: false, Salary: false });
  };

  const handleNewChange = (e, field) => {
    const value = e.target.value;
    setNewSalary((prev) => ({ ...prev, [field]: value }));
    setAddErrors((prev) => ({ ...prev, [field]: value.trim() === "" }));
  };

  const saveNewSalary = async () => {
    console.log(newSalary.dep_id);
    const newAddErrors = {
      dep_id: !newSalary.dep_id || newSalary.dep_id.trim() === "",
      Position: !newSalary.Position || newSalary.Position.trim() === "",
      Salary: !newSalary.Salary || newSalary.Salary.trim() === ""
    };

    if (newAddErrors.dep_id || newAddErrors.Position || newAddErrors.Salary) {
      setAddErrors(newAddErrors);
      return;
    }

    try {
      await axios.post("http://localhost:5000/salary", {
        dep_id: newSalary.dep_id,
        Position: newSalary.Position,
        Salary: newSalary.Salary
      });
      await getUsers();
      setIsAdding(false);
      setNewSalary({ dep_id: "", Position: "", Salary: "" });
      setAddErrors({ dep_id: false, Position: false, Salary: false });
    } catch (error) {
      console.error("Error adding salary:", error.response || error);
    }
  };

  // Render (unchanged)
  return (
    <div style={{ position: "relative" }}>
      {/* Background Image and Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1
        }}
      />

      <div className="column container mt-5" style={{ position: "relative", zIndex: 1 }}>
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input mt-2"
            style={{ maxWidth: "300px" }}
            placeholder="Search by Job Title or Position..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="mt-2 is-flex">
            {!isMobile && !isAdding && (
              <button className="button is-success ml-3" onClick={startAdding}>
                Add Position
              </button>
            )}
            {isMobile ? (
              <Link to="/home/AddSalary" className="button is-success ml-3">
                Add Position
              </Link>
            ) : (
              <div>
                <AddSalaryModal
                  isOpen={isModalActive}
                  onClose={() => setIsModalActive(false)}
                />
              </div>
            )}
            <button onClick={handleViewPDF} className="button is-info ml-3">
              View PDF
            </button>
          </div>
        </div>

        {!isMobile ? (
          <table className="table is-striped is-fullwidth">
            <thead>
              <tr>
                <th>Salary ID</th>
                <th>Job Title</th>
                <th>Position / Level</th>
                <th>Salary</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {isAdding && (
                <tr>
                  <td>New</td>
                  <td>
                    <div className="select is-fullwidth">
                      <select
                        value={newSalary.dep_id}
                        onChange={(e) => handleNewChange(e, "dep_id")}
                        required
                        style={{ borderColor: addErrors.dep_id ? "red" : "" }}
                      >
                        <option value="">Select a Job</option>
                        {jobs.map((dep) => (
                          <option key={dep.id} value={dep.id}>
                            {dep.Job_Title}
                          </option>
                        ))}
                      </select>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="input"
                      value={newSalary.Position}
                      onChange={(e) => handleNewChange(e, "Position")}
                      placeholder="Position / Level"
                      required
                      style={{ borderColor: addErrors.Position ? "red" : "" }}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      className="input"
                      value={newSalary.Salary}
                      onChange={(e) => handleNewChange(e, "Salary")}
                      placeholder="Salary"
                      required
                      style={{ borderColor: addErrors.Salary ? "red" : "" }}
                    />
                  </td>
                  <td>
                    <button
                      className="button is-small is-success mr-2"
                      onClick={saveNewSalary}
                    >
                      Save
                    </button>
                    <button
                      className="button is-small is-danger"
                      onClick={cancelAdding}
                    >
                      Cancel
                    </button>
                  </td>
                </tr>
              )}
              {displayedSalaries.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>
                    {editingId === user.id ? (
                      <div className="select is-fullwidth">
                        <select
                          value={editedSalary.dep_id || ""}
                          onChange={(e) => handleEditChange(e, "dep_id")}
                          required
                          style={{ borderColor: editErrors.dep_id ? "red" : "" }}
                        >
                          <option value="">Select a Job</option>
                          {jobs.map((dep) => (
                            <option key={dep.id} value={dep.id}>
                              {dep.Job_Title}
                            </option>
                          ))}
                        </select>
                      </div>
                    ) : (
                      user.Job_Title
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="text"
                        className="input"
                        value={editedSalary.Position || ""}
                        onChange={(e) => handleEditChange(e, "Position")}
                        style={{ borderColor: editErrors.Position ? "red" : "" }}
                      />
                    ) : (
                      user.position
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <input
                        type="number"
                        className="input"
                        value={editedSalary.Salary || ""}
                        onChange={(e) => handleEditChange(e, "Salary")}
                        style={{ borderColor: editErrors.Salary ? "red" : "" }}
                      />
                    ) : (
                      `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                        minimumFractionDigits: 2
                      })}`
                    )}
                  </td>
                  <td>
                    {editingId === user.id ? (
                      <>
                        <button
                          className="button is-small is-success mr-2"
                          onClick={() => saveSalary(user.id)}
                        >
                          Save
                        </button>
                        <button
                          className="button is-small is-danger"
                          onClick={cancelEditing}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="button is-small is-info"
                        onClick={() => startEditing(user)}
                      >
                        Edit
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div className="columns is-multiline">
            {displayedSalaries.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{user.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p><strong>Salary ID:</strong> {user.id}</p>
                    <p><strong>Position:</strong> {user.position}</p>
                    <p>
                      <strong>Salary:</strong>{" "}
                      {user.salary
                        ? `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                            minimumFractionDigits: 2
                          })}`
                        : "N/A"}
                    </p>
                  </div>
                  <footer className="card-footer">
                    <Link
                      to={`/home/editSalary/${user.id}`}
                      className="card-footer-item button is-small is-info"
                    >
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredSalaries.length}>All</option>
              </select>
            </div>
          </div>
          <p>Showing {displayedSalaries.length} of {filteredSalaries.length} entries</p>
        </div>
      </div>
    </div>
  );
}

export default SalaryList;
