import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import AddDeptModal from "../UI/Modal/AddDeptModal";

function DepartList() {
  /*** State Hooks ***/
  const [jobs, setJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingJobId, setEditingJobId] = useState(null);
  const [editedJobTitle, setEditedJobTitle] = useState("");
  const [isModalActive, setIsModalActive] = useState(false); // Modal state

  /*** Effect Hooks ***/
  useEffect(() => {
    fetchJobs();

    // Handle screen size changes
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  /*** API Calls ***/
  const fetchJobs = async () => {
    try {
      const response = await axios.get("http://localhost:5000/job");
      setJobs(response.data);
    } catch (error) {
      console.error("Error fetching job titles:", error);
    }
  };

  return (
    <div style={{ position: "relative" }}>
      {/* Background and Overlay */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundImage: "url('/loginBanner.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          backgroundAttachment: "fixed",
          zIndex: -2,
        }}
      />
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          background: "rgba(0, 0, 0, 0.5)",
          zIndex: -1,
        }}
      />

      {/* Main Content */}
      <div
        className="column container mt-5"
        style={{ position: "relative", zIndex: 1 }}
      >
        {/* Search & Actions */}
        <div className="is-flex is-justify-content-space-between mb-3 is-flex-wrap-wrap">
          <input
            type="text"
            className="input"
            style={{ maxWidth: "300px" }}
            placeholder="Search Job Title..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          {isMobile ? (
            <Link to="/home/AddJob" className="button is-success ml-3">
              Add Job Title
            </Link>
          ) : (
            <div>
              <button
                className="button is-success ml-3"
                onClick={() => setIsModalActive(true)}
              >
                Add Job Title
              </button>
              <AddDeptModal
                isActive={isModalActive}
                onClose={() => setIsModalActive(false)}
                onDepartmentAdded={fetchJobs} // Pass the function to refresh the table
              />
            </div>
          )}
        </div>

        {/* Table View */}
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>Job ID</th>
              <th>Job Title</th>
            </tr>
          </thead>
          <tbody>
            {jobs.slice(0, entriesToShow).map((job) => (
              <tr key={job.id}>
                <td>{job.id}</td>
                <td>{job.Job_Title}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Footer - Entry Controls */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={jobs.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {jobs.slice(0, entriesToShow).length} of {jobs.length}{" "}
            entries
          </p>
        </div>
      </div>
    </div>
  );
}

export default DepartList;
