import React, { useState, useEffect } from "react";
import axios from "axios";
import { pdf } from "@react-pdf/renderer";
import SalaryPDF from "../Printing/SalaryPDF";
import { Link } from "react-router-dom";

function SalaryList() {
  const [salaries, setSalaries] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [entriesToShow, setEntriesToShow] = useState(10);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [editingId, setEditingId] = useState(null);
  const [editedSalary, setEditedSalary] = useState({});

  useEffect(() => {
    getSalaries();
    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const getSalaries = async () => {
    try {
      const response = await axios.get("http://localhost:5000/salary");
      setSalaries(response.data);
    } catch (error) {
      console.error("Error fetching salaries:", error);
    }
  };

  const startEditing = (salary) => {
    setEditingId(salary.id);
    setEditedSalary({ ...salary });
  };

  const cancelEditing = () => {
    setEditingId(null);
    setEditedSalary({});
  };

  const handleEditChange = (e, field) => {
    setEditedSalary({ ...editedSalary, [field]: e.target.value });
  };

  const saveSalary = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/salary/${id}`, editedSalary);
      getSalaries();
      setEditingId(null);
    } catch (error) {
      console.error("Error updating salary:", error);
    }
  };

  const filteredSalaries = salaries.filter((user) =>
    `${user.Job_Title} ${user.position}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  const displayedSalaries = filteredSalaries.slice(0, entriesToShow);

  const handleViewPDF = async () => {
    const blob = await pdf(<SalaryPDF salaries={salaries} />).toBlob();
    const url = URL.createObjectURL(blob);
    window.open(url);
  };

  return (
    <div className="container mt-5">
      <input
        type="text"
        className="input mb-3"
        placeholder="Search by Job Title or Position..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleViewPDF} className="button is-info ml-3">
        View PDF
      </button>

      {!isMobile ? (
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>Salary ID</th>
              <th>Job Title</th>
              <th>Position / Level</th>
              <th>Salary</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {displayedSalaries.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>
                  {editingId === user.id ? (
                    <input
                      type="text"
                      className="input"
                      value={editedSalary.Job_Title || ""}
                      onChange={(e) => handleEditChange(e, "Job_Title")}
                    />
                  ) : (
                    user.Job_Title
                  )}
                </td>
                <td>
                  {editingId === user.id ? (
                    <input
                      type="text"
                      className="input"
                      value={editedSalary.position || ""}
                      onChange={(e) => handleEditChange(e, "position")}
                    />
                  ) : (
                    user.position
                  )}
                </td>
                <td>
                  {editingId === user.id ? (
                    <input
                      type="number"
                      className="input"
                      value={editedSalary.salary || ""}
                      onChange={(e) => handleEditChange(e, "salary")}
                    />
                  ) : (
                    `₱${parseFloat(user.salary).toLocaleString("en-PH", {
                      minimumFractionDigits: 2,
                    })}`
                  )}
                </td>
                <td>
                  {editingId === user.id ? (
                    <>
                      <button
                        className="button is-small is-success mr-2"
                        onClick={() => saveSalary(user.id)}
                      >
                        Save
                      </button>
                      <button
                        className="button is-small is-danger"
                        onClick={cancelEditing}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <button
                      className="button is-small is-info"
                      onClick={() => startEditing(user)}
                    >
                      Edit
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
          <div className="columns is-multiline">
            {displayedSalaries.map((user) => (
              <div key={user.id} className="column is-12">
                <div className="card">
                  <header className="card-header">
                    <p className="card-header-title">{user.Job_Title}</p>
                  </header>
                  <div className="card-content">
                    <p><strong>Salary ID:</strong> {user.id}</p>
                    <p><strong>Position:</strong> {user.position}</p>
                    <p><strong>Salary:</strong> {user.salary ? `₱${parseFloat(user.salary).toLocaleString("en-PH", { minimumFractionDigits: 2 })}` : "N/A"}</p>
                  </div>
                  <footer className="card-footer">
                    <Link to={`/home/editSalary/${user.id}`} className="card-footer-item button is-small is-info">
                      Edit
                    </Link>
                  </footer>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer Controls */}
        <div className="is-flex is-align-items-center is-justify-content-space-between mt-3 is-flex-wrap-wrap has-text-white">
          <div>
            <label className="mr-2">Show entries:</label>
            <div className="select">
              <select
                value={entriesToShow}
                onChange={(e) => setEntriesToShow(Number(e.target.value))}
              >
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="20">20</option>
                <option value={filteredSalaries.length}>All</option>
              </select>
            </div>
          </div>
          <p>
            Showing {displayedSalaries.length} of {filteredSalaries.length} entries
          </p>
        </div>
      </div>
  );
}

export default SalaryList;
